// src/api/horasApi.ts
import axios from 'axios';

const API_BASE_URL = 'http://backend.yeisonduque.top';

/* =========================
   Tipos base (ajusta si quieres)
   ========================= */
export interface DailyActivity {
  id: string;
  date: string;              // "YYYY-MM-DD"
  employee_id: number;
  project_code: string;
  project_name: string;
  phase: string;
  discipline: string;
  activity: string;
  hours: number;
  note?: string;
}

export interface HourData {
  id?: string;               // <- presente SOLO al editar
  project_code: string;
  phase: string;
  discipline: string;
  activity: string;
  hours: number | string;
  date: string;              // "YYYY-MM-DD"
  employee_id?: number | string; // requerido en creación
  note?: string;
}

/* =========================
   Helpers
   ========================= */
const normalizeHours = (h: number | string | undefined) =>
  typeof h === 'string' ? parseFloat(h.replace(',', '.')) : (h ?? 0);

const sanitizeCreate = (data: HourData) => {
  return {
    project_code: String(data.project_code ?? ''),
    phase: String(data.phase ?? ''),
    discipline: String(data.discipline ?? ''),
    activity: String(data.activity ?? ''),
    hours: normalizeHours(data.hours),
    date: String(data.date ?? ''),
    employee_id:
      typeof data.employee_id === 'string'
        ? parseInt(data.employee_id, 10)
        : Number(data.employee_id),
    note: data.note ? String(data.note) : '',
  };
};

// Para PUT no mandamos campos no editables como employee_id, id, project_name
const sanitizeUpdate = (data: HourData) => {
  return {
    project_code: String(data.project_code ?? ''),
    phase: String(data.phase ?? ''),
    discipline: String(data.discipline ?? ''),
    activity: String(data.activity ?? ''),
    hours: normalizeHours(data.hours),
    date: String(data.date ?? ''),
    note: data.note ? String(data.note) : '',
  };
};

const encodeId = (id: string | number) => encodeURIComponent(String(id));

/* =========================
   Crear/Actualizar (botón Guardar/Actualizar)
   ========================= */
export const submitHours = async (data: HourData) => {
  try {
    if (data.id) {
      // UPDATE
      const id = encodeId(data.id); // <- NO convertir a número
      const payload = sanitizeUpdate(data);
      console.log('Actualizando hora:', { id, payload });
      const res = await axios.put(`${API_BASE_URL}/hours/${id}`, payload);
      return res.data;
    } else {
      // CREATE
      const payload = sanitizeCreate(data);
      console.log('Creando hora:', payload);
      const res = await axios.post(`${API_BASE_URL}/hours/`, payload);
      return res.data;
    }
  } catch (error: any) {
    console.error('Error al enviar las horas:', error);
    if (axios.isAxiosError(error) && error.response) {
      console.error('Detalles del error (submitHours):', error.response.data);
      const detail =
        (error.response.data as any)?.detail ??
        (error.response.data as any)?.message;
      if (detail) throw new Error(typeof detail === 'string' ? detail : JSON.stringify(detail));
    }
    throw error;
  }
};

// ---------- Catálogos / dependencias ----------
export const getProjects = async () => {
  const res = await axios.get(`${API_BASE_URL}/projects/`);
  return res.data;
};

export const getEmployees = async () => {
  const res = await axios.get(`${API_BASE_URL}/employees/`);
  return res.data;
};

export const getProjectStages = async (projectCode: string) => {
  const encoded = encodeURIComponent(projectCode);
  const res = await axios.get(
    `${API_BASE_URL}/activities/project/${encoded}/stages`
  );
  return res.data;
};

export const getDisciplinesByStage = async (projectCode: string, stage: string) => {
  const params = [encodeURIComponent(projectCode), encodeURIComponent(stage)].join('::');
  const res = await axios.get(`${API_BASE_URL}/activities/${params}/disciplines`);
  return res.data;
};

export const getActivitiesByDiscipline = async (
  projectCode: string,
  stage: string,
  discipline: string
) => {
  const params = [
    encodeURIComponent(projectCode),
    encodeURIComponent(stage),
    encodeURIComponent(discipline),
  ].join('::');
  const res = await axios.get(`${API_BASE_URL}/activities/${params}/activities`);
  return res.data;
};

// ---------- Consultas de horas ----------
export const getDailyActivities = async (
  date: Date | string,
  employeeId: number | string
): Promise<DailyActivity[]> => {
  // YYYY-MM-DD
  const dateStr = date instanceof Date ? date.toISOString().split('T')[0] : date;
  const employeeIdNum =
    typeof employeeId === 'string' ? parseInt(employeeId, 10) : employeeId;

  const res = await axios.get<DailyActivity[]>(`${API_BASE_URL}/daily-activities`, {
    params: { date: dateStr, employee_id: employeeIdNum },
    validateStatus: (s) => s >= 200 && s < 300,
  });
  return res.data || [];
};

// ---------- Update/Delete directos ----------
export const updateHour = async (hourId: number, data: any) => {
  try {
    const response = await axios.put(`${API_BASE_URL}/hours/${hourId}`, data);
    return response.data;
  } catch (error) {
    console.error(`Error al actualizar el registro de horas ${hourId}:`, error);
    throw error;
  }
};

export const deleteHour = async (hourId: number) => {
  try {
    const response = await axios.delete(`${API_BASE_URL}/hours/${hourId}`);
    return response.data;
  } catch (error) {
    console.error(`Error al eliminar el registro de horas ${hourId}:`, error);
    throw error;
  }
};

// ---------- Otros ----------
export const getProjectByCode = async (projectCode: string) => {
  const encoded = encodeURIComponent(projectCode);
  const res = await axios.get(`${API_BASE_URL}/projects/${encoded}`);
  return res.data;
};

export const loginUser = async (credentials: any) => {
  const res = await axios.post(`${API_BASE_URL}/auth/login`, credentials);
  return res.data;
};
