import React from 'react';
import { motion } from 'framer-motion';

interface SelectFieldProps {
  label: string;
  name: string;
  value: string;
  onChange: (e: React.ChangeEvent<HTMLSelectElement>) => void;
  options: { value: string; label: string }[];
  isLoading?: boolean;
  disabled?: boolean;
  required?: boolean;
  id?: string;
  error?: string;
}

const SelectField: React.FC<SelectFieldProps> = ({ 
  label, 
  name, 
  value, 
  onChange, 
  options, 
  isLoading = false, 
  disabled = false,
  required = false,
  id = name,
  error = ''
}) => {
  // Convertir a string para ARIA
  const ariaRequired = required ? "true" : "false";
  const ariaDisabled = (disabled || isLoading) ? "true" : "false";

  return (
    <div className="relative">
      <label 
        htmlFor={id}
        className="block text-sm font-medium text-gray-700 mb-1"
      >
        {label} {required && <span className="text-red-500">*</span>}
      </label>
      
      <div className="relative">
        <select
          id={id}
          name={name}
          value={value}
          onChange={onChange}
          disabled={disabled || isLoading}
          required={required}
          aria-required={required ? "true" : "false"}
          aria-disabled={(disabled || isLoading) ? "true" : "false"}
          aria-invalid={!!error}
          aria-describedby={error ? `${id}-error` : undefined}
          className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:outline-none transition-all ${
            disabled 
              ? 'bg-gray-100 text-gray-400 border-gray-200' 
              : 'bg-white text-gray-800 border-gray-300 focus:border-indigo-500 focus:ring-indigo-200'
          } ${
            error ? 'border-red-500 focus:border-red-500 focus:ring-red-200' : ''
          } ${isLoading ? 'pr-10' : ''}`}
        >
          {options.map((option, index) => (
            <option 
                key={`${option.value}-${index}`} // Usar índice para key única
                value={option.value}
            >
                {option.label}
            </option>
            ))}
        </select>
        
        {isLoading && (
          <div className="absolute right-3 top-1/2 transform -translate-y-1/2">
            <motion.div
              animate={{ rotate: 360 }}
              transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
              className="w-5 h-5 border-t-2 border-indigo-600 border-r-2 border-transparent rounded-full"
              aria-label="Cargando opciones"
            />
          </div>
        )}
      </div>
      
      {error && (
        <p id={`${id}-error`} className="mt-1 text-sm text-red-600">
          {error}
        </p>
      )}
      
      {disabled && !isLoading && (
        <div 
          className="absolute inset-0 bg-gray-50 bg-opacity-50 rounded-lg cursor-not-allowed"
          aria-hidden="true"
        />
      )}
    </div>
  );
};

export default SelectField;
