// horasApi.ts
import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000'; // Asumiendo que tu FastAPI se ejecuta en el puerto 8000

export const submitHours = async (data: any) => {
    try {
        const response = await axios.post(`${API_BASE_URL}/hours/`, data);
        return response.data;
    } catch (error) {
        console.error('Error al enviar las horas:', error);
        throw error;
    }
};

export const getProjects = async () => {
    try {
        const response = await axios.get(`${API_BASE_URL}/projects/`);
        return response.data;
    } catch (error) {
        console.error('Error al obtener proyectos:', error);
        throw error;
    }
};

export const getEmployees = async () => {
    try {
        const response = await axios.get(`${API_BASE_URL}/employees/`);
        return response.data;
    } catch (error) {
        console.error('Error al obtener empleados:', error);
        throw error;
    }
};

export const getProjectStages = async (projectCode: string) => {
    try {
        const response = await axios.get(`${API_BASE_URL}/projects/${projectCode}/stages`);
        return response.data;
    } catch (error) {
        console.error(`Error al obtener etapas para el proyecto ${projectCode}:`, error);
        throw error;
    }
};

export const getDisciplinesByStage = async (projectCode: string, stage: string) => {
    try {
        const response = await axios.get(`${API_BASE_URL}/activities/${projectCode}/${stage}/disciplines`);
        return response.data;
    } catch (error) {
        console.error(`Error al obtener disciplinas para el proyecto ${projectCode}, etapa ${stage}:`, error);
        throw error;
    }
};

export const getActivitiesByDiscipline = async (projectCode: string, stage: string, discipline: string) => {
    try {
        const response = await axios.get(`${API_BASE_URL}/activities/${projectCode}/${stage}/${discipline}/activities`);
        return response.data;
    } catch (error) {
        console.error(`Error al obtener actividades para el proyecto ${projectCode}, etapa ${stage}, disciplina ${discipline}:`, error);
        throw error;
    }
};
