import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { toast } from 'react-toastify';
import SelectField from './SelectField';
import InputField from './InputField';
import TextareaField from './TextareaField';
import ActividadesList from './ActividadesList';
import DatePicker from './DatePicker';

const FormularioHoras: React.FC = () => {
  const [formData, setFormData] = useState({
    fecha: new Date().toISOString().split('T')[0],
    colaborador: '',
    codigo: '',
    proyecto: '',
    etapa: '',
    disciplina: '',
    actividad: '',
    horas: '',
    nota: '',
  });

  const [errors, setErrors] = useState<Record<string, string>>({});
  const [actividadesRegistradas, setActividadesRegistradas] = useState<any[]>([]);
  const [proyectos, setProyectos] = useState<any[]>([]);
  const [etapas, setEtapas] = useState<any[]>([]);
  const [disciplinas, setDisciplinas] = useState<any[]>([]);
  const [actividades, setActividades] = useState<any[]>([]);
  const [isLoading, setIsLoading] = useState({
    proyectos: true,
    etapas: false,
    disciplinas: false,
    actividades: false,
  });

  // Simulaci贸n de carga de datos desde API
  useEffect(() => {
    // Simular carga de proyectos
    setTimeout(() => {
      setProyectos([
        { id: 'B-Interno', name: 'Irisa' },
        { id: 'P-0010', name: 'Proyecto Residencial' },
        { id: 'P-0038', name: 'Centro Comercial' },
        { id: 'P-0045', name: 'Hospital Regional' },
      ]);
      setIsLoading(prev => ({ ...prev, proyectos: false }));
    }, 800);
  }, []);

  // Cargar etapas cuando cambia el proyecto
  useEffect(() => {
    if (formData.proyecto) {
      setIsLoading(prev => ({ ...prev, etapas: true }));
      // Simular carga de etapas
      setTimeout(() => {
        setEtapas([
          'SIN',
          'Dise帽o Conceptual',
          'Desarrollo de Proyecto',
          'Ejecuci贸n',
          'Postventa'
        ]);
        setIsLoading(prev => ({ ...prev, etapas: false }));
      }, 600);
    }
  }, [formData.proyecto]);

  // Cargar disciplinas cuando cambia la etapa
  useEffect(() => {
    if (formData.etapa) {
      setIsLoading(prev => ({ ...prev, disciplinas: true }));
      // Simular carga de disciplinas
      setTimeout(() => {
        setDisciplinas([
          'N/A - No Aplica',
          'Arquitectura',
          'Estructural',
          'MEP',
          'Coordinaci贸n BIM'
        ]);
        setIsLoading(prev => ({ ...prev, disciplinas: false }));
      }, 500);
    }
  }, [formData.etapa]);

  // Cargar actividades cuando cambia la disciplina
  useEffect(() => {
    if (formData.disciplina) {
      setIsLoading(prev => ({ ...prev, actividades: true }));
      // Simular carga de actividades
      setTimeout(() => {
        setActividades([
          'Permisos personales',
          'Reuni贸n de coordinaci贸n',
          'Modelado 3D',
          'Planos de construcci贸n',
          'Control de calidad'
        ]);
        setIsLoading(prev => ({ ...prev, actividades: false }));
      }, 400);
    }
  }, [formData.disciplina]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    
    // Limpiar error al cambiar
    if (errors[name]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[name];
        return newErrors;
      });
    }
  };

  const handleDateChange = (date: string) => {
    setFormData(prev => ({ ...prev, fecha: date }));
  };

  const validateForm = () => {
    const newErrors: Record<string, string> = {};
    
    if (!formData.colaborador) {
      newErrors.colaborador = 'Seleccione un colaborador';
    }
    
    if (!formData.proyecto) {
      newErrors.proyecto = 'Seleccione un proyecto';
    }
    
    if (!formData.actividad) {
      newErrors.actividad = 'Seleccione una actividad';
    }
    
    if (!formData.horas) {
      newErrors.horas = 'Ingrese las horas trabajadas';
    } else if (parseFloat(formData.horas) <= 0) {
      newErrors.horas = 'Las horas deben ser mayores a 0';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!validateForm()) {
      toast.error('Por favor complete los campos obligatorios');
      return;
    }
    
    // Agregar nueva actividad
    const nuevaActividad = {
      id: Date.now(),
      ...formData,
      proyectoNombre: proyectos.find(p => p.id === formData.proyecto)?.name || formData.proyecto
    };
    
    setActividadesRegistradas(prev => [...prev, nuevaActividad]);
    
    // Resetear parte del formulario
    setFormData(prev => ({
      ...prev,
      etapa: '',
      disciplina: '',
      actividad: '',
      horas: '',
      nota: ''
    }));
    
    // Limpiar errores
    setErrors({});
    
    toast.success('Actividad registrada con 茅xito!');
  };

  const handleCancel = () => {
    setFormData({
      fecha: new Date().toISOString().split('T')[0],
      colaborador: '',
      codigo: '',
      proyecto: '',
      etapa: '',
      disciplina: '',
      actividad: '',
      horas: '',
      nota: '',
    });
    setErrors({});
  };

  return (
    <motion.div 
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      className="bg-white rounded-xl shadow-lg overflow-hidden"
    >
      <div className="p-6 bg-gradient-to-r from-indigo-600 to-purple-700 text-white">
        <h2 className="text-2xl font-bold">REGISTRO DE HORAS</h2>
        <p className="text-indigo-200">Complete el formulario para registrar sus horas de trabajo</p>
      </div>
      
      <form onSubmit={handleSubmit} className="p-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
          <DatePicker 
            label="FECHA" 
            value={formData.fecha} 
            onChange={handleDateChange} 
          />
          
          <SelectField
            id="colaborador-field"
            label="COLABORADOR"
            name="colaborador"
            value={formData.colaborador}
            onChange={handleChange}
            options={[
              { value: '', label: 'Seleccione colaborador' },
              { value: 'TD001', label: 'Totani Dugas Data' },
              { value: 'JM002', label: 'Juan Martinez' },
              { value: 'AP003', label: 'Ana P茅rez' },
              { value: 'CR004', label: 'Carlos Rodr铆guez' }
            ]}
            required
            error={errors.colaborador}
          />
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
          <SelectField
            id="codigo-field"
            label="CDIGO"
            name="codigo"
            value={formData.codigo}
            onChange={handleChange}
            options={[
              { value: '', label: 'Seleccione c贸digo' },
              { value: 'B-Interno', label: 'B-Interno' },
              { value: 'P-0010', label: 'P-0010' },
              { value: 'P-0038', label: 'P-0038' },
              { value: 'P-0045', label: 'P-0045' }
            ]}
          />
          
          <SelectField
            id="proyecto-field"
            label="PROYECTO"
            name="proyecto"
            value={formData.proyecto}
            onChange={handleChange}
            options={[
              { value: '', label: 'Seleccione proyecto' },
              ...proyectos.map(p => ({ value: p.id, label: p.name }))
            ]}
            isLoading={isLoading.proyectos}
            required
            error={errors.proyecto}
          />
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
          <SelectField
            id="etapa-field"
            label="ETAPA"
            name="etapa"
            value={formData.etapa}
            onChange={handleChange}
            options={[
              { value: '', label: 'Seleccione etapa' },
              ...etapas.map(e => ({ value: e, label: e }))
            ]}
            isLoading={isLoading.etapas}
            disabled={!formData.proyecto}
          />
          
          <SelectField
            id="disciplina-field"
            label="DISCIPLINA"
            name="disciplina"
            value={formData.disciplina}
            onChange={handleChange}
            options={[
              { value: '', label: 'Seleccione disciplina' },
              ...disciplinas.map(d => ({ value: d, label: d }))
            ]}
            isLoading={isLoading.disciplinas}
            disabled={!formData.etapa}
          />
          
          <SelectField
            id="actividad-field"
            label="ACTIVIDAD"
            name="actividad"
            value={formData.actividad}
            onChange={handleChange}
            options={[
              { value: '', label: 'Seleccione actividad' },
              ...actividades.map(a => ({ value: a, label: a }))
            ]}
            isLoading={isLoading.actividades}
            disabled={!formData.disciplina}
            required
            error={errors.actividad}
          />
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          <InputField
            label="HORAS"
            name="horas"
            type="number"
            value={formData.horas}
            onChange={handleChange}
            placeholder="0.0"
            min="0.5"
            max="12"
            step="0.5"
            required
            error={errors.horas}
          />
          
          <TextareaField
            label="NOTA (OPCIONAL)"
            name="nota"
            value={formData.nota}
            onChange={handleChange}
            placeholder="Ingrese notas adicionales..."
            rows={2}
          />
        </div>
        
        <div className="flex justify-between mt-8">
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="button"
            onClick={handleCancel}
            className="px-8 py-3 bg-gray-200 text-gray-700 rounded-lg font-medium shadow-md hover:bg-gray-300 transition-colors"
          >
            CANCELAR
          </motion.button>
          
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="submit"
            className="px-8 py-3 bg-gradient-to-r from-indigo-600 to-purple-600 text-white rounded-lg font-medium shadow-md hover:from-indigo-700 hover:to-purple-700 transition-all"
          >
            REGISTRAR
          </motion.button>
        </div>
      </form>
      
      <div className="border-t border-gray-200 mt-6">
        <div className="p-6">
          <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center">
            <span className="mr-2"></span> LISTADO DE ACTIVIDADES
          </h3>
          
          <ActividadesList 
            actividades={actividadesRegistradas} 
            setActividades={setActividadesRegistradas}
          />
        </div>
      </div>
    </motion.div>
  );
};

export default FormularioHoras;
