import React from 'react';
import FormularioHoras from './components/FormularioHoras';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function App() {
  // Función para manejar el éxito del formulario, por ejemplo, mostrar una notificación
  const handleFormSuccess = () => {
    // Aquí puedes añadir lógica, como recargar una lista de horas o mostrar un mensaje global.
    console.log("Formulario enviado exitosamente en App.tsx!");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-blue-50 py-8 px-4">
        
        {/* Pasa la prop onSuccess requerida al componente FormularioHoras */}
        <FormularioHoras onSuccess={handleFormSuccess} />
     
    </div>
  );
}

export default App;
