import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { format, parseISO } from 'date-fns';
import { es } from 'date-fns/locale';

interface DatePickerProps {
  label: string;
  // CAMBIADO: 'value' a 'selectedDate' y su tipo a Date | null
  selectedDate: Date | null;
  // CAMBIADO: el tipo de 'onChange' para aceptar Date | null
  onChange: (date: Date | null) => void;
  required?: boolean;
}

const DatePicker: React.FC<DatePickerProps> = ({ label, selectedDate, onChange, required = false }) => {
  const [showCalendar, setShowCalendar] = useState(false);
  // Usa selectedDate directamente si está presente, si no, usa la fecha actual
  const displayDate = selectedDate || new Date(); 

  const days = ['Dom', 'Lun', 'Mar', 'Mié', 'Jue', 'Vie', 'Sáb'];
  const months = [
    'Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio',
    'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre'
  ];

  const getDaysInMonth = (year: number, month: number) => {
    return new Date(year, month + 1, 0).getDate();
  };

  const getFirstDayOfMonth = (year: number, month: number) => {
    return new Date(year, month, 1).getDay();
  };

  const handlePrevMonth = () => {
    const newDate = new Date(displayDate);
    newDate.setMonth(displayDate.getMonth() - 1);
    // Llama a onChange con el nuevo objeto Date, no con una cadena
    onChange(newDate); 
  };

  const handleNextMonth = () => {
    const newDate = new Date(displayDate);
    newDate.setMonth(displayDate.getMonth() + 1);
    // Llama a onChange con el nuevo objeto Date, no con una cadena
    onChange(newDate);
  };

  const handleDayClick = (day: number) => {
    const newDate = new Date(displayDate.getFullYear(), displayDate.getMonth(), day);
    // Llama a onChange con el nuevo objeto Date, no con una cadena
    onChange(newDate); 
    setShowCalendar(false); // Cierra el calendario después de seleccionar una fecha
  };

  const renderDays = () => {
    const totalDays = getDaysInMonth(displayDate.getFullYear(), displayDate.getMonth());
    const firstDay = getFirstDayOfMonth(displayDate.getFullYear(), displayDate.getMonth());
    const daysArray = [];

    // Espacios en blanco para los días anteriores al primer día del mes
    for (let i = 0; i < firstDay; i++) {
      daysArray.push(<div key={`empty-${i}`} className="text-center p-2"></div>);
    }

    // Días del mes
    for (let i = 1; i <= totalDays; i++) {
      const isSelected = selectedDate && 
                         selectedDate.getFullYear() === displayDate.getFullYear() &&
                         selectedDate.getMonth() === displayDate.getMonth() &&
                         selectedDate.getDate() === i;

      daysArray.push(
        <button
          key={i}
          type="button"
          onClick={() => handleDayClick(i)}
          className={`text-center text-sm p-2 rounded-full transition-colors 
            ${isSelected ? 'bg-indigo-600 text-white' : 'hover:bg-gray-100'}
            ${!isSelected && 'text-gray-900'}
          `}
        >
          {i}
        </button>
      );
    }
    return daysArray;
  };

  return (
    <div className="relative">
      <label className="block text-sm font-medium text-gray-700 mb-1">
        {label} {required && <span className="text-red-500">*</span>}
      </label>
      <input
        type="text"
        readOnly
        // USA selectedDate para el valor del input
        value={selectedDate ? format(selectedDate, 'dd/MM/yyyy', { locale: es }) : ''}
        onClick={() => setShowCalendar(!showCalendar)}
        className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent cursor-pointer"
        aria-label={label}
        aria-haspopup="dialog"
        aria-expanded={showCalendar}
      />

      <AnimatePresence>
        {showCalendar && (
          <motion.div
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            className="absolute z-10 bg-white p-4 rounded-lg shadow-lg mt-2 border border-gray-200"
            style={{ width: '100%', maxWidth: '300px' }} // Ajusta el ancho si es necesario
          >
            <div className="flex justify-between items-center mb-4">
                  <button
                    type="button"
                    onClick={handlePrevMonth}
                    className="p-2 rounded-full hover:bg-gray-100"
                    aria-label="Mes anterior"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-gray-600" viewBox="0 0 20 20" fill="currentColor">
                      <path fillRule="evenodd" d="M12.707 5.293a1 1 0 010 1.414L9.414 10l3.293 3.293a1 1 0 01-1.414 1.414l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 0z" clipRule="evenodd" />
                    </svg>
                  </button>
                  
                  <div className="font-medium text-gray-800" aria-live="polite">
                    {months[displayDate.getMonth()]} {displayDate.getFullYear()}
                  </div>
                  
                  <button
                    type="button"
                    onClick={handleNextMonth}
                    className="p-2 rounded-full hover:bg-gray-100"
                    aria-label="Mes siguiente"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-gray-600" viewBox="0 0 20 20" fill="currentColor">
                      <path fillRule="evenodd" d="M7.293 14.707a1 1 0 010-1.414L10.586 10 7.293 6.707a1 1 0 011.414-1.414l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414 0z" clipRule="evenodd" />
                    </svg>
                  </button>
                </div>
                
                <div className="grid grid-cols-7 gap-1 mb-2">
                  {days.map(day => (
                    <div key={day} className="text-center text-xs text-gray-500 font-medium py-1">
                      {day}
                    </div>
                  ))}
                </div>
                
                <div className="grid grid-cols-7 gap-1">
                  {renderDays()}
                </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default DatePicker;
