import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { format, parseISO } from 'date-fns';
import { es } from 'date-fns/locale';

interface DatePickerProps {
  label: string;
  value: string;
  onChange: (date: string) => void;
}

const DatePicker: React.FC<DatePickerProps> = ({ label, value, onChange }) => {
  const [showCalendar, setShowCalendar] = useState(false);
  const date = value ? parseISO(value) : new Date();

  const days = ['Dom', 'Lun', 'Mar', 'Mié', 'Jue', 'Vie', 'Sáb'];
  const months = [
    'Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio',
    'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre'
  ];

  const getDaysInMonth = (year: number, month: number) => {
    return new Date(year, month + 1, 0).getDate();
  };

  const getFirstDayOfMonth = (year: number, month: number) => {
    return new Date(year, month, 1).getDay();
  };

  const handlePrevMonth = () => {
    const newDate = new Date(date);
    newDate.setMonth(date.getMonth() - 1);
    onChange(newDate.toISOString().split('T')[0]);
  };

  const handleNextMonth = () => {
    const newDate = new Date(date);
    newDate.setMonth(date.getMonth() + 1);
    onChange(newDate.toISOString().split('T')[0]);
  };

  const handleDayClick = (day: number) => {
    const newDate = new Date(date.getFullYear(), date.getMonth(), day);
    onChange(newDate.toISOString().split('T')[0]);
    setShowCalendar(false);
  };

  const renderCalendar = () => {
    const year = date.getFullYear();
    const month = date.getMonth();
    const daysInMonth = getDaysInMonth(year, month);
    const firstDayOfMonth = getFirstDayOfMonth(year, month);
    
    const daysArray = [];
    
    // Días del mes anterior
    const prevMonthDays = getDaysInMonth(year, month - 1);
    for (let i = prevMonthDays - firstDayOfMonth + 1; i <= prevMonthDays; i++) {
      daysArray.push(
        <div key={`prev-${i}`} className="text-center text-gray-400 py-2">
          {i}
        </div>
      );
    }
    
    // Días del mes actual
    for (let i = 1; i <= daysInMonth; i++) {
      const isCurrentDay = i === date.getDate() && 
                           month === new Date().getMonth() && 
                           year === new Date().getFullYear();
      const isSelectedDay = i === date.getDate();
      
      daysArray.push(
        <motion.button
          key={i}
          type="button"
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
          className={`w-full py-2 rounded-full ${
            isSelectedDay 
              ? 'bg-indigo-600 text-white' 
              : isCurrentDay 
                ? 'bg-indigo-100 text-indigo-700' 
                : 'text-gray-700 hover:bg-gray-100'
          }`}
          onClick={() => handleDayClick(i)}
          aria-label={`Seleccionar día ${i} de ${months[month]} ${year}`}
        >
          {i}
        </motion.button>
      );
    }
    
    // Días del próximo mes
    const totalCells = 42; // 6 filas * 7 columnas
    const remainingCells = totalCells - daysArray.length;
    for (let i = 1; i <= remainingCells; i++) {
      daysArray.push(
        <div key={`next-${i}`} className="text-center text-gray-400 py-2">
          {i}
        </div>
      );
    }
    
    return daysArray;
  };

  return (
    <div>
      <label className="block text-sm font-medium text-gray-700 mb-1">
        {label}
      </label>
      
      <div className="relative">
        <button
          type="button"
          onClick={() => setShowCalendar(!showCalendar)}
          className="w-full px-4 py-3 border border-gray-300 rounded-lg bg-white text-left focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all"
          aria-expanded={showCalendar}
          aria-controls="calendar-container"
          aria-label={`Fecha seleccionada: ${format(date, "EEEE, d 'de' MMMM 'de' yyyy", { locale: es })}`}
        >
          {format(date, "EEEE, d 'de' MMMM 'de' yyyy", { locale: es })}
        </button>
        
        <AnimatePresence>
          {showCalendar && (
            <motion.div
              id="calendar-container"
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.2 }}
              className="absolute z-10 mt-1 w-full bg-white border border-gray-200 rounded-lg shadow-lg overflow-hidden"
              role="dialog"
              aria-modal="true"
              aria-label="Calendario para seleccionar fecha"
            >
              <div className="p-4">
                <div className="flex justify-between items-center mb-4">
                  <button
                    type="button"
                    onClick={handlePrevMonth}
                    className="p-2 rounded-full hover:bg-gray-100"
                    aria-label="Mes anterior"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-gray-600" viewBox="0 0 20 20" fill="currentColor">
                      <path fillRule="evenodd" d="M12.707 5.293a1 1 0 010 1.414L9.414 10l3.293 3.293a1 1 0 01-1.414 1.414l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 0z" clipRule="evenodd" />
                    </svg>
                  </button>
                  
                  <div className="font-medium text-gray-800" aria-live="polite">
                    {months[date.getMonth()]} {date.getFullYear()}
                  </div>
                  
                  <button
                    type="button"
                    onClick={handleNextMonth}
                    className="p-2 rounded-full hover:bg-gray-100"
                    aria-label="Mes siguiente"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-gray-600" viewBox="0 0 20 20" fill="currentColor">
                      <path fillRule="evenodd" d="M7.293 14.707a1 1 0 010-1.414L10.586 10 7.293 6.707a1 1 0 011.414-1.414l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414 0z" clipRule="evenodd" />
                    </svg>
                  </button>
                </div>
                
                <div className="grid grid-cols-7 gap-1 mb-2">
                  {days.map(day => (
                    <div key={day} className="text-center text-xs text-gray-500 font-medium py-1">
                      {day}
                    </div>
                  ))}
                </div>
                
                <div className="grid grid-cols-7 gap-1">
                  {renderCalendar()}
                </div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default DatePicker;
