// src/components/FormularioHoras.tsx
import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { format, parseISO, getDay } from 'date-fns';
import { es } from 'date-fns/locale';
import Header from './Header';

import { toast } from 'sonner';
import {
    Calendar as CalendarIcon,
    Briefcase,
    Trash2,
    LogOut,
    Save,
    Pencil,
    ListChecks,
    ClipboardList,
    Clock,
    XCircle,
    PlusCircle,
    Loader2,
} from 'lucide-react';

import { Card, CardContent, CardHeader, CardTitle } from './ui/card';
import { Button } from './ui/button';
import { Input } from './ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from './ui/select';
import { Badge } from './ui/badge';
import { Progress } from './ui/progress';
import { Skeleton } from './ui/skeleton';
import { Calendar } from './ui/calendar';
import { Popover, PopoverContent, PopoverTrigger } from './ui/popover';
import { cn } from '../lib/utils';
import { FormSelect } from './formulario/FormSelect';
import { ActivityItem } from './formulario/ActivityItem';
import { ActivityListSkeleton } from './formulario/ActivityListSkeleton';
import { EmptyState } from './formulario/EmptyState';
import { TotalHoursProgress } from './formulario/TotalHoursProgress';
import {
    submitHours,
    getProjects,
    getProjectStages,
    getDisciplinesByStage,
    getActivitiesByDiscipline,
    getDailyActivities,
    deleteHour,
} from '../api/horasApi';

// Interfaces
interface FormularioHorasProps {
    onSuccess: () => void;
    employeeId: number;
    employeeName: string;
    onLogout: () => void;
}

interface Project {
    code: string;
    name: string;
}

interface Activity {
    id: number;
    date: string;
    employee_id: number;
    project_code: string;
    project_name: string;
    phase: string;
    discipline: string;
    activity: string;
    hours: number;
    note: string;
}

interface FormSelectProps {
    label: string;
    name: string;
    value: string;
    onValueChange: (name: string, value: string) => void;
    options: { value: string; label: string }[];
    placeholder: string;
    loading: boolean;
    disabled: boolean;
    icon: React.ReactNode;
    required?: boolean;
}

interface ActivityItemProps {
    activity: Activity;
    onEdit: (activity: Activity) => void;
    onDelete: (id: number) => void;
}

interface TotalHoursProgressProps {
    totalHours: number;
    selectedDate: Date;
}

const initialFormData = (employeeId: number) => ({
    employee_id: String(employeeId),
    project_code: '',
    phase: '',
    discipline: '',
    activity: '',
    hours: '',
    note: '',
});

// Main Component
const FormularioHoras: React.FC<FormularioHorasProps> = ({ onSuccess, employeeId, employeeName, onLogout }) => {
    // State Management
    const [formData, setFormData] = useState(initialFormData(employeeId));
    const [selectedDate, setSelectedDate] = useState(new Date());
    const [dailyActivities, setDailyActivities] = useState<Activity[]>([]);
    const [projects, setProjects] = useState<Project[]>([]);
    const [stages, setStages] = useState<string[]>([]);
    const [disciplines, setDisciplines] = useState<string[]>([]);
    const [activities, setActivities] = useState<string[]>([]);
    const [editingActivityId, setEditingActivityId] = useState<number | null>(null);

    // Loading States
    const [loading, setLoading] = useState({
        submit: false,
        projects: true,
        stages: false,
        disciplines: false,
        activities: false,
        dailyActivities: true,
    });

    // Memoized Values
    const totalHoursToday = useMemo(() =>
        dailyActivities.reduce((sum, activity) => sum + activity.hours, 0),
        [dailyActivities]
    );

    const selectedProjectName = useMemo(() =>
        projects.find(p => p.code === formData.project_code)?.name || '',
        [formData.project_code, projects]
    );

    // Data Fetching
    const refreshDailyActivities = useCallback(async (date: Date, employeeId: string) => {
        setLoading(prev => ({ ...prev, dailyActivities: true }));
        try {
            const dateString = format(date, 'yyyy-MM-dd');
            const activities = await getDailyActivities(dateString, Number(employeeId));
            setDailyActivities(activities);
        } catch (error) {
            toast.error('Error al cargar las actividades del día.');
            console.error('Error fetching daily activities:', error);
        } finally {
            setLoading(prev => ({ ...prev, dailyActivities: false }));
        }
    }, []);

    useEffect(() => {
        refreshDailyActivities(selectedDate, String(employeeId));
    }, [selectedDate, employeeId, refreshDailyActivities]);

    useEffect(() => {
        const fetchProjects = async () => {
            setLoading(prev => ({ ...prev, projects: true }));
            try {
                const data = await getProjects();
                setProjects(data);
            } catch (error) {
                toast.error('Error al cargar los proyectos.');
                console.error('Error fetching projects:', error);
            } finally {
                setLoading(prev => ({ ...prev, projects: false }));
            }
        };
        fetchProjects();
    }, []);

    useEffect(() => {
        if (!formData.project_code) {
            setStages([]);
            setDisciplines([]);
            setActivities([]);
            return;
        }
        const fetchStages = async (projectCode: string) => {
            setLoading(prev => ({ ...prev, stages: true }));
            try {
                const data = await getProjectStages(projectCode);
                setStages(data.map(String));
                return data;
            } catch (error) {
                toast.error('Error al cargar las etapas del proyecto.');
                return [];
            } finally {
                setLoading(prev => ({ ...prev, stages: false }));
            }
        };
        fetchStages(formData.project_code);
    }, [formData.project_code]);

    useEffect(() => {
        if (!formData.phase) {
            setDisciplines([]);
            setActivities([]);
            return;
        }
        const fetchDisciplines = async (projectCode: string, phase: string) => {
            setLoading(prev => ({ ...prev, disciplines: true }));
            try {
                const data = await getDisciplinesByStage(projectCode, phase);
                setDisciplines(data.map(String));
                return data;
            } catch (error) {
                toast.error('Error al cargar las disciplinas.');
                return [];
            } finally {
                setLoading(prev => ({ ...prev, disciplines: false }));
            }
        };
        fetchDisciplines(formData.project_code, formData.phase);
    }, [formData.project_code, formData.phase]);

    useEffect(() => {
        if (!formData.discipline) {
            setActivities([]);
            return;
        }
        const fetchActivities = async (projectCode: string, phase: string, discipline: string) => {
            setLoading(prev => ({ ...prev, activities: true }));
            try {
                const data = await getActivitiesByDiscipline(projectCode, phase, discipline);
                setActivities(data.map(String));
                return data;
            } catch (error) {
                toast.error('Error al cargar las actividades.');
                return [];
            } finally {
                setLoading(prev => ({ ...prev, activities: false }));
            }
        };
        fetchActivities(formData.project_code, formData.phase, formData.discipline);
    }, [formData.project_code, formData.phase, formData.discipline]);

    // Handlers
    const handleDateChange = (date: Date | undefined) => {
        if (date) {
            setSelectedDate(date);
            refreshDailyActivities(date, String(employeeId));
        }
    };

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    // Data Fetching
    const fetchProjects = useCallback(async () => {
        setLoading(prev => ({ ...prev, projects: true }));
        try {
            const projectData = await getProjects();
            setProjects(projectData);
        } catch (error) {
            toast.error('Error al cargar proyectos.');
        } finally {
            setLoading(prev => ({ ...prev, projects: false }));
        }
    }, []);

    const fetchStages = useCallback(async (projectCode: string) => {
        setLoading(prev => ({ ...prev, stages: true }));
        try {
            const stageData = await getProjectStages(projectCode);
            setStages(stageData);
        } catch (error) {
            toast.error('Error al cargar etapas.');
        } finally {
            setLoading(prev => ({ ...prev, stages: false }));
        }
    }, []);

    const fetchDisciplines = useCallback(async (projectCode: string, stage: string) => {
        setLoading(prev => ({ ...prev, disciplines: true }));
        try {
            const disciplineData = await getDisciplinesByStage(projectCode, stage);
            setDisciplines(disciplineData);
        } catch (error) {
            toast.error('Error al cargar disciplinas.');
        } finally {
            setLoading(prev => ({ ...prev, disciplines: false }));
        }
    }, []);

    const fetchActivities = useCallback(async (projectCode: string, stage: string, discipline: string) => {
        setLoading(prev => ({ ...prev, activities: true }));
        try {
            const activityData = await getActivitiesByDiscipline(projectCode, stage, discipline);
            setActivities(activityData);
        } catch (error) {
            toast.error('Error al cargar actividades.');
        } finally {
            setLoading(prev => ({ ...prev, activities: false }));
        }
    }, []);

    useEffect(() => {
        fetchProjects();
    }, [fetchProjects]);

    useEffect(() => {
        refreshDailyActivities(selectedDate, String(employeeId));
    }, [selectedDate, employeeId, refreshDailyActivities]);

    const handleSelectChange = (name: string, value: string) => {
        const newFormData = { ...formData, [name]: value };

        if (name === 'project_code') {
            newFormData.phase = '';
            newFormData.discipline = '';
            newFormData.activity = '';
            setStages([]);
            setDisciplines([]);
            setActivities([]);
            if (value) {
                fetchStages(value);
            }
        } else if (name === 'phase') {
            newFormData.discipline = '';
            newFormData.activity = '';
            setDisciplines([]);
            setActivities([]);
            if (value) {
                fetchDisciplines(newFormData.project_code, value);
            }
        } else if (name === 'discipline') {
            newFormData.activity = '';
            setActivities([]);
            if (value) {
                fetchActivities(newFormData.project_code, newFormData.phase, value);
            }
        }
        setFormData(newFormData);
    };

    const resetForm = useCallback(() => {
        setFormData(initialFormData(employeeId));
        setEditingActivityId(null);
    }, [employeeId]);

    const handleEditActivity = async (activity: Activity) => {
        setFormData({
            employee_id: String(activity.employee_id),
            project_code: activity.project_code,
            phase: activity.phase,
            discipline: activity.discipline,
            activity: activity.activity,
            hours: String(activity.hours),
            note: activity.note || '',
        });
        setEditingActivityId(activity.id);

        // Forzar la actualización de los selects dependientes
        try {
            await fetchStages(activity.project_code);
            await fetchDisciplines(activity.project_code, activity.phase);
            await fetchActivities(activity.project_code, activity.phase, activity.discipline);
        } catch (error) {
            console.error('Error al cargar los datos para edición:', error);
        }

        window.scrollTo({ top: 0, behavior: 'smooth' });
    };

    const handleDelete = async (idToDelete: number) => {
        toast.warning('¿Estás seguro de que quieres eliminar esta actividad?', {
            duration: 5000,
            action: {
                label: 'Eliminar',
                onClick: async () => {
                    try {
                        await deleteHour(idToDelete);
                        toast.error('Actividad eliminada correctamente.', { duration: 3000 });
                        refreshDailyActivities(selectedDate, String(employeeId));
                        if (editingActivityId === idToDelete) {
                            resetForm();
                        }
                    } catch (error) {
                        toast.error('Error al eliminar la actividad.', { duration: 3000 });
                        console.error('Error deleting activity:', error);
                    }
                },
            },
            cancel: {
                label: 'Cancelar',
                onClick: () => toast.dismiss(),
            },
        });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setLoading(prev => ({ ...prev, submit: true }));

        const submissionData = {
            ...formData,
            date: format(selectedDate, 'yyyy-MM-dd'),
            project_name: selectedProjectName,
            hours: Number(formData.hours),
            employee_id: employeeId,
            id: editingActivityId ?? undefined,
        };

        try {
            await submitHours(submissionData);
            toast.success(`Actividad ${editingActivityId ? 'actualizada' : 'guardada'} con éxito.`,
            {
                duration: 3000,
                style: {
                    backgroundColor: '#28a745',
                    color: 'white',
                    border: 'none'
                }
            });
            onSuccess();
            resetForm();
            refreshDailyActivities(selectedDate, String(employeeId));
        } catch (error) {
            toast.error('Error al guardar la actividad.', { duration: 3000 });
            console.error('Error submitting hours:', error);
        } finally {
            setLoading(prev => ({ ...prev, submit: false }));
        }
    };

    const isFormValid = useMemo(() => {
        return Boolean(
            formData.project_code?.trim() &&
            formData.phase?.trim() &&
            formData.discipline?.trim() &&
            formData.activity?.trim() &&
            formData.hours &&
            !isNaN(parseFloat(formData.hours)) &&
            parseFloat(formData.hours) > 0
        );
    }, [formData]);

    // Render
    return (
        <div>
            <Header userName={employeeName} onLogout={onLogout} />
            <div className="container mx-auto p-4 sm:p-6 lg:p-8">


            <div className="grid grid-cols-1 lg:grid-cols-5 gap-8">
                <main className="lg:col-span-3">
                    <Card>
                        <CardHeader>
                            <CardTitle className="flex items-center justify-center">
                                {editingActivityId ? <Pencil className="mr-2 h-5 w-5" /> : <PlusCircle className="mr-2 h-5 w-5" />}
                                {editingActivityId ? 'Editar Actividad' : 'Registrar Actividad'}
                            </CardTitle>
                        </CardHeader>
                        <CardContent>
                            <form onSubmit={handleSubmit} className="space-y-6">
                                <div className="w-full md:w-1/3">
                                    <label htmlFor="date" className="block text-sm font-medium text-foreground/80 mb-1">Fecha</label>
                                    <Popover>
                                        <PopoverTrigger asChild>
                                            <Button
                                                variant={"outline"}
                                                className={cn(
                                                    "w-full justify-start text-left font-normal",
                                                    !selectedDate && "text-muted-foreground"
                                                )}
                                            >
                                                <CalendarIcon className="mr-2 h-4 w-4" />
                                                {selectedDate ? format(selectedDate, "PPP", { locale: es }) : <span>Seleccione una fecha</span>}
                                            </Button>
                                        </PopoverTrigger>
                                        <PopoverContent className="w-auto p-0">
                                            <Calendar
                                                mode="single"
                                                selected={selectedDate}
                                                onSelect={handleDateChange}
                                                initialFocus
                                                locale={es}
                                            />
                                        </PopoverContent>
                                    </Popover>
                                </div>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                    <FormSelect
                                        label="Código de Proyecto"
                                        name="project_code"
                                        value={formData.project_code}
                                        onValueChange={handleSelectChange}
                                        options={projects.map(p => ({ value: p.code, label: `${p.code} - ${p.name}` }))}
                                        placeholder="Seleccione un proyecto"
                                        loading={loading.projects}
                                        disabled={loading.projects}
                                        icon={<Briefcase className="h-4 w-4" />}
                                        required
                                    />
                                    <div className="space-y-2">
                                        <label className="block text-sm font-medium text-foreground/80 mb-1">Nombre del Proyecto</label>
                                        <Input value={selectedProjectName} readOnly className="bg-muted/50" />
                                    </div>
                                    <FormSelect
                                        label="Etapa"
                                        name="phase"
                                        value={formData.phase}
                                        onValueChange={handleSelectChange}
                                        options={stages.map(s => ({ value: s, label: s }))}
                                        placeholder="Seleccione una etapa"
                                        loading={loading.stages}
                                        disabled={!formData.project_code || loading.stages}
                                        icon={<ListChecks className="h-4 w-4" />}
                                        required
                                    />
                                    <FormSelect
                                        label="Disciplina"
                                        name="discipline"
                                        value={formData.discipline}
                                        onValueChange={handleSelectChange}
                                        options={disciplines.map(d => ({ value: d, label: d }))}
                                        placeholder="Seleccione una disciplina"
                                        loading={loading.disciplines}
                                        disabled={!formData.phase || loading.disciplines}
                                        icon={<Pencil className="h-4 w-4" />}
                                        required
                                    />
                                    <FormSelect
                                        label="Actividad"
                                        name="activity"
                                        value={formData.activity}
                                        onValueChange={handleSelectChange}
                                        options={activities.map(a => ({ value: a, label: a }))}
                                        placeholder="Seleccione una actividad"
                                        loading={loading.activities}
                                        disabled={!formData.discipline || loading.activities}
                                        icon={<ClipboardList className="h-4 w-4" />}
                                        required
                                    />
                                    <div className="space-y-2">
                                        <label htmlFor="hours" className="block text-sm font-medium text-foreground/80 mb-1">Horas <span className="text-destructive">*</span></label>
                                        <Input
                                            id="hours"
                                            name="hours"
                                            type="number"
                                            value={formData.hours}
                                            onChange={handleChange}
                                            min="0.5"
                                            step="0.5"
                                            required
                                        />
                                    </div>
                                </div>

                                <div className="space-y-2">
                                    <label htmlFor="note" className="block text-sm font-medium text-foreground/80 mb-1">Nota (Opcional)</label>
                                    <Input
                                        id="note"
                                        name="note"
                                        type="text"
                                        value={formData.note}
                                        onChange={handleChange}
                                    />
                                </div>

                                <div className="flex justify-end space-x-2 pt-4">
                                    {editingActivityId && (
                                        <Button type="button" variant="outline" onClick={resetForm}>
                                            <XCircle className="mr-2 h-4 w-4" />
                                            Cancelar Edición
                                        </Button>
                                    )}
                                    <Button 
                                        type="submit" 
                                        disabled={!isFormValid || loading.submit} 
                                        className={`min-w-[150px] transition-opacity ${!isFormValid ? 'opacity-70 cursor-not-allowed' : ''}`}
                                    >
                                        {loading.submit ? (
                                            <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                                        ) : (
                                            <Save className="mr-2 h-4 w-4" />
                                        )}
                                        {editingActivityId ? 'Actualizar' : 'Guardar'}
                                    </Button>
                                </div>
                            </form>
                        </CardContent>
                    </Card>
                </main>

                <aside className="lg:col-span-2">
                    <Card>
                        <CardHeader>
                            <CardTitle className="text-center">Actividades del Día</CardTitle>
                        </CardHeader>
                        <CardContent>
                            <div className="space-y-4">
                                {loading.dailyActivities ? (
                                    <ActivityListSkeleton />
                                ) : dailyActivities.length > 0 ? (
                                    dailyActivities.map(activity => (
                                        <ActivityItem
                                            key={activity.id}
                                            activity={activity}
                                            onEdit={handleEditActivity}
                                            onDelete={handleDelete}
                                        />
                                    ))
                                ) : (
                                    <EmptyState />
                                )}
                            </div>
                            <div className="mt-6">
                                <TotalHoursProgress totalHours={totalHoursToday} selectedDate={selectedDate} />
                            </div>
                        </CardContent>
                    </Card>
                </aside>
            </div>
        </div>

    </div>
    );
};

export default FormularioHoras;
