import React, { useState, useEffect, useRef, ChangeEvent } from 'react';
import InputField from './InputField';
import SelectField from './SelectField';
import TextareaField from './TextareaField';
import DatePicker from './DatePicker';
import { 
  submitHours, 
  getProjects, 
  getEmployees, 
  getProjectStages, 
  getDisciplinesByStage, 
  getActivitiesByDiscipline,
  getDailyActivities,
  updateHour,
  deleteHour
} from '../api/horasApi';

// Interfaces
interface FormState {
    date: string;
    employee_id: string;
    project_code: string;
    phase: string;
    discipline: string;
    activity: string;
    hours: string;
    note: string;
}

interface Employee {
    id: number;
    name: string;
}

interface Project {
    code: string;
    name: string;
}

interface Activity {
    id: number;
    date: string;
    employee_id: number;
    project_code: string;
    project_name: string;
    phase: string;
    discipline: string;
    activity: string;
    hours: number;
    note?: string;
}

interface FormularioHorasProps {
    onSuccess: () => void;
}

const initialFormState: FormState = {
    date: '',
    employee_id: '',
    project_code: '',
    phase: '',
    discipline: '',
    activity: '',
    hours: '',
    note: '',
};

const FormularioHoras: React.FC<FormularioHorasProps> = ({ onSuccess }) => {
    // State declarations
    const [loadingDailyActivities, setLoadingDailyActivities] = useState<boolean>(true);
    const [dailyActivities, setDailyActivities] = useState<Activity[]>([]);
    const [formData, setFormData] = useState<FormState>(initialFormState);
    const [selectedProjectName, setSelectedProjectName] = useState<string>('');
    const [employees, setEmployees] = useState<Employee[]>([]);
    const [loadingEmployees, setLoadingEmployees] = useState<boolean>(true);
    const [stages, setStages] = useState<string[]>([]);
    const [loadingStages, setLoadingStages] = useState<boolean>(false);
    const [disciplines, setDisciplines] = useState<string[]>([]);
    const [loadingDisciplines, setLoadingDisciplines] = useState<boolean>(false);
    const [activities, setActivities] = useState<string[]>([]);
    const [loadingActivities, setLoadingActivities] = useState<boolean>(false);
    const [editingActivityId, setEditingActivityId] = useState<number | null>(null);
    const [selectedEmployeeName, setSelectedEmployeeName] = useState<string>('');
    const [formattedDate, setFormattedDate] = useState<string>('');

    // Refs
    const formRef = useRef<HTMLDivElement>(null);
    const [submitError, setSubmitError] = useState<string | null>(null);
    const [selectedDate, setSelectedDate] = useState<Date | null>(new Date());
    const [projects, setProjects] = useState<Project[]>([]);
    const [loadingProjects, setLoadingProjects] = useState<boolean>(true);

    // Formatear fecha para mostrar
    useEffect(() => {
        if (selectedDate) {
            const options: Intl.DateTimeFormatOptions = { year: 'numeric', month: 'long', day: 'numeric' };
            setFormattedDate(selectedDate.toLocaleDateString('es-ES', options));
        }
    }, [selectedDate]);

    // Cargar empleados y proyectos al montar
    useEffect(() => {
        const fetchInitialData = async () => {
            try {
                const [employeesData, projectsData] = await Promise.all([
                    getEmployees(),
                    getProjects()
                ]);
                setEmployees(employeesData);
                setProjects(projectsData);
            } catch (error) {
                console.error('Error fetching initial data:', error);
            } finally {
                setLoadingEmployees(false);
                setLoadingProjects(false);
            }
        };
        fetchInitialData();
    }, []);

    // Cargar actividades diarias cuando cambia la fecha o el empleado
    useEffect(() => {
        if (selectedDate && formData.employee_id) {
            refreshDailyActivities();
        } else {
            setDailyActivities([]);
        }
    }, [selectedDate, formData.employee_id]);

    // Cargar etapas cuando cambia el proyecto
    useEffect(() => {
        if (formData.project_code) {
            setLoadingStages(true);
            getProjectStages(formData.project_code)
                .then(setStages)
                .catch((err) => console.error('Error fetching stages:', err))
                .finally(() => setLoadingStages(false));
        } else {
            setStages([]);
        }
        setFormData(prev => ({ ...prev, phase: '', discipline: '', activity: '' }));
    }, [formData.project_code]);

    // Cargar disciplinas cuando cambia la etapa
    useEffect(() => {
        if (formData.phase) {
            setLoadingDisciplines(true);
            getDisciplinesByStage(formData.project_code, formData.phase)
                .then(setDisciplines)
                .catch((err) => console.error('Error fetching disciplines:', err))
                .finally(() => setLoadingDisciplines(false));
        } else {
            setDisciplines([]);
        }
        setFormData(prev => ({ ...prev, discipline: '', activity: '' }));
    }, [formData.phase]);

    // Cargar actividades cuando cambia la disciplina
    useEffect(() => {
        if (formData.discipline) {
            setLoadingActivities(true);
            getActivitiesByDiscipline(formData.project_code, formData.phase, formData.discipline)
                .then(setActivities)
                .catch((err) => console.error('Error fetching activities:', err))
                .finally(() => setLoadingActivities(false));
        } else {
            setActivities([]);
        }
        setFormData(prev => ({ ...prev, activity: '' }));
    }, [formData.discipline]);

    const refreshDailyActivities = async () => {
        if (selectedDate && formData.employee_id) {
            setLoadingDailyActivities(true);
            try {
                const dateStr = selectedDate.toISOString().split('T')[0];
                const activities = await getDailyActivities(dateStr, parseInt(formData.employee_id, 10));
                setDailyActivities(activities);
            } catch (error) {
                console.error('Error fetching daily activities:', error);
            } finally {
                setLoadingDailyActivities(false);
            }
        }
    };

    const handleDateChange = (date: Date | null) => {
        setSelectedDate(date);
    };

    const handleChange = (e: ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));

        if (name === 'employee_id') {
            const employee = employees.find(emp => emp.id.toString() === value);
            setSelectedEmployeeName(employee ? employee.name : '');
        }
    };

    const handleProjectCodeChange = (e: ChangeEvent<HTMLSelectElement>) => {
        const { value } = e.target;
        setFormData(prev => ({ ...prev, project_code: value, phase: '', discipline: '', activity: '' }));
        const project = projects.find(p => p.code === value);
        setSelectedProjectName(project ? project.name : '');
    };

    const handleSelectActivity = (activity: Activity) => {
        setEditingActivityId(activity.id);
        setFormData({
            date: activity.date,
            employee_id: activity.employee_id.toString(),
            project_code: activity.project_code,
            phase: activity.phase,
            discipline: activity.discipline,
            activity: activity.activity,
            hours: activity.hours.toString(),
            note: activity.note || '',
        });

        const employee = employees.find(emp => emp.id === activity.employee_id);
        setSelectedEmployeeName(employee ? employee.name : '');

        const project = projects.find(p => p.code === activity.project_code);
        setSelectedProjectName(project ? project.name : '');

        if (formRef.current) {
            formRef.current.scrollIntoView({ behavior: 'smooth' });
        }
    };

    const resetForm = () => {
        setFormData(initialFormState);
        setEditingActivityId(null);
        setSubmitError(null);
        setSelectedEmployeeName('');
        setSelectedProjectName('');
        // No reseteamos la fecha ni el empleado seleccionado para mantener el contexto
        if (formRef.current) {
            formRef.current.scrollIntoView({ behavior: 'smooth' });
        }
    };

    const handleSubmit = async () => {
        if (!selectedDate) {
            setSubmitError('Por favor, seleccione una fecha.');
            return;
        }

        const dataToSubmit = {
            ...formData,
            date: selectedDate ? selectedDate.toISOString().split('T')[0] : '',
            hours: parseFloat(formData.hours) || 0,
        };

        // Validar campos
        for (const key in dataToSubmit) {
            if (key !== 'note' && !dataToSubmit[key as keyof typeof dataToSubmit]) {
                const friendlyKey = key.replace('_', ' ');
                setSubmitError(`El campo ${friendlyKey} es obligatorio.`);
                return;
            }
        }

        setSubmitError(null);

        try {
            if (editingActivityId) {
                await updateHour(editingActivityId, dataToSubmit);
                alert('Registro actualizado con éxito');
            } else {
                await submitHours(dataToSubmit);
                alert('Registro guardado con éxito');
            }
            resetForm();
            refreshDailyActivities();
            onSuccess();
        } catch (error) {
            console.error('Error submitting form:', error);
            setSubmitError('Error al guardar el registro. Por favor, inténtelo de nuevo.');
        }
    };

    const handleDelete = async () => {
        if (editingActivityId) {
            if (window.confirm('¿Está seguro de que desea eliminar este registro?')) {
                try {
                    await deleteHour(editingActivityId);
                    alert('Registro eliminado con éxito');
                    resetForm();
                    refreshDailyActivities();
                    onSuccess(); // Llama a la función onSuccess para actualizar el resumen
                } catch (error) {
                    console.error('Error deleting hour:', error);
                    setSubmitError('Error al eliminar el registro. Por favor, inténtelo de nuevo.');
                }
            }
        }
    };

    return (
        <div className="p-4 md:p-8 bg-gray-50 min-h-screen">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 max-w-7xl mx-auto">
                {/* Columna del Formulario */}
                <div>
                    <div ref={formRef} className="bg-white rounded-xl shadow-lg overflow-hidden border border-[#083c4c]">
                        <div className="bg-[#083c4c] text-white p-4">
                            <h2 className="text-xl font-bold text-center">REGISTRO DE HORAS</h2>
                        </div>

                        <div className="p-6">
                            {submitError && (
                                <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4" role="alert">
                                    <strong className="font-bold">Error: </strong>
                                    <span className="block sm:inline">{submitError}</span>
                                </div>
                            )}

                            {/* Sección de Fecha y Empleado */}
                            <div className="mb-6">
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                    <div>
                                        <DatePicker
                                            label="FECHA"
                                            selectedDate={selectedDate}
                                            onChange={handleDateChange}
                                            className="w-full p-2 border border-gray-300 rounded-md shadow-sm"
                                        />
                                    </div>
                                    <div>
                                        <SelectField
                                            label="EMPLEADO"
                                            name="employee_id"
                                            value={formData.employee_id}
                                            onChange={handleChange}
                                            options={employees.map((emp: Employee) => ({ value: emp.id.toString(), label: emp.name }))}
                                            isLoading={loadingEmployees}
                                        />
                                    </div>
                                </div>
                            </div>

                            {/* Sección de Proyecto y Etapa */}
                            <div className="mb-6">
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                    <div>
                                        <SelectField
                                            label="PROYECTO"
                                            name="project_code"
                                            value={formData.project_code}
                                            onChange={handleProjectCodeChange}
                                            options={projects.map((proj: Project) => ({ value: proj.code, label: `${proj.code} - ${proj.name}` }))}
                                            isLoading={loadingProjects}
                                        />
                                    </div>
                                    <div>
                                        <SelectField
                                            label="ETAPA"
                                            name="phase"
                                            value={formData.phase}
                                            onChange={handleChange}
                                            options={stages.map((stage: string) => ({ value: stage, label: stage }))}
                                            isLoading={loadingStages}
                                            disabled={!formData.project_code}
                                        />
                                    </div>
                                </div>
                            </div>

                            {/* Sección de Disciplina y Actividad */}
                            <div className="mb-6">
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                    <div>
                                        <SelectField
                                            label="DISCIPLINA"
                                            name="discipline"
                                            value={formData.discipline}
                                            onChange={handleChange}
                                            options={disciplines.map((disc: string) => ({ value: disc, label: disc }))}
                                            isLoading={loadingDisciplines}
                                            disabled={!formData.phase}
                                        />
                                    </div>
                                    <div>
                                        <SelectField
                                            label="ACTIVIDAD"
                                            name="activity"
                                            value={formData.activity}
                                            onChange={handleChange}
                                            options={activities.map((act: string) => ({ value: act, label: act }))}
                                            isLoading={loadingActivities}
                                            disabled={!formData.discipline}
                                        />
                                    </div>
                                </div>
                            </div>

                            {/* Sección de Horas */}
                            <div className="mb-6">
                                <InputField
                                    label="HORAS"
                                    name="hours"
                                    type="number"
                                    value={formData.hours}
                                    onChange={handleChange}
                                />
                            </div>

                            {/* Sección de Nota */}
                            <div className="mb-6">
                                <TextareaField
                                    label="NOTA"
                                    name="note"
                                    value={formData.note}
                                    onChange={handleChange}
                                />
                            </div>

                            {/* Botones de Acción */}
                            <div className="flex justify-between items-center">
                                <div className="flex-grow">
                                    <button 
                                        onClick={handleSubmit} 
                                        className="w-full bg-[#FBE205] text-[#083c4c] font-bold py-3 px-4 rounded-lg hover:bg-yellow-400 transition-colors duration-300 shadow-md"
                                    >
                                        {editingActivityId ? 'Actualizar Registro' : 'Guardar Registro'}
                                    </button>
                                </div>
                                <div className="flex-shrink-0 ml-4">
                                    <button 
                                        onClick={resetForm} 
                                        className="text-gray-500 hover:text-gray-800 font-semibold py-2 px-4 rounded-lg transition-colors duration-300"
                                    >
                                        Cancelar
                                    </button>
                                </div>
                                {editingActivityId && (
                                    <div className="flex-shrink-0 ml-4">
                                        <button 
                                            onClick={handleDelete} 
                                            className="bg-red-600 text-white font-semibold py-2 px-4 rounded-lg hover:bg-red-700 transition-colors duration-300"
                                        >
                                            Eliminar
                                        </button>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                </div>

                {/* Listado de Actividades Registradas */}
                <div>
                    <div className="bg-white rounded-xl shadow-lg overflow-hidden border border-[#083c4c] h-full">
                        <div className="bg-[#083c4c] text-white p-4">
                            <h2 className="text-xl font-bold text-center">LISTADO DE ACTIVIDADES</h2>
                        </div>

                        <div className="p-4 overflow-y-auto max-h-[calc(100vh-200px)]">
                            {loadingDailyActivities ? (
                                <div className="text-center py-8">
                                    <div className="animate-spin rounded-full h-10 w-10 border-b-2 border-[#083c4c] mx-auto"></div>
                                    <p className="mt-3 text-gray-600">Cargando actividades...</p>
                                </div>
                            ) : dailyActivities.length === 0 ? (
                                <div className="text-center py-8 bg-gray-50 rounded-lg">
                                    <div className="text-gray-400 mb-3">
                                        <svg xmlns="http://www.w3.org/2000/svg" className="h-16 w-16 mx-auto" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-3 7h3m-3 4h3m-6-4h.01M9 16h.01" />
                                        </svg>
                                    </div>
                                    <p className="text-gray-500">No hay actividades registradas para este día</p>
                                </div>
                            ) : (
                                <div className="space-y-4">
                                    {dailyActivities.map((activity: Activity) => (
                                        <div 
                                            key={activity.id} 
                                            className={`bg-white border rounded-lg p-4 shadow-sm border-[#083c4c] cursor-pointer hover:bg-gray-50 transition-colors duration-200 ${editingActivityId === activity.id ? 'ring-2 ring-yellow-400' : ''}`}
                                            onClick={() => handleSelectActivity(activity)}
                                        >
                                            <div className="flex justify-between items-start mb-3">
                                                <h4 className="font-bold text-lg text-[#083c4c]">{activity.activity}</h4>
                                                <span className="text-sm text-gray-500">{activity.date}</span>
                                            </div>

                                            <div className="text-sm text-gray-700">
                                                <p className="font-semibold mb-1">Proyecto {activity.project_name}</p>

                                                <div className="grid grid-cols-3 gap-2 mt-3">
                                                    <div>
                                                        <div className="text-gray-500 text-xs">ETAPA</div>
                                                        <div>{activity.phase}</div>
                                                    </div>

                                                    <div>
                                                        <div className="text-gray-500 text-xs">DISCIPLINA</div>
                                                        <div>{activity.discipline}</div>
                                                    </div>

                                                    <div>
                                                        <div className="text-gray-500 text-xs">HORAS</div>
                                                        <div className="font-bold text-[#083c4c]">{activity.hours} hrs</div>
                                                    </div>
                                                </div>

                                                {activity.note && (
                                                    <div className="mt-4 pt-3 border-t border-gray-100">
                                                        <div className="text-gray-500 text-xs">NOTA</div>
                                                        <div>{activity.note}</div>
                                                    </div>
                                                )}
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default FormularioHoras;
