import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DatePicker from './DatePicker'; // Asumiendo que DatePicker.tsx está en el mismo directorio
import InputField from './InputField'; // Asumiendo que InputField.tsx está en el mismo directorio
import SelectField from './SelectField'; // Asumiendo que SelectField.tsx está en el mismo directorio
import TextareaField from './TextareaField'; // Asumiendo que TextareaField.tsx está en el mismo directorio
import ActividadesList from './ActividadesList'; // Importa el nuevo componente

interface FormularioHorasProps {
    onSuccess: () => void;
}

interface Actividad {
    id: string;
    description: string;
}

interface Employee {
    id: string; // Assuming 'id' is the unique identifier for an employee
    short_name: string;
    // Add other properties if they exist in your employee object
}


const FormularioHoras: React.FC<FormularioHorasProps> = ({ onSuccess }) => {
    const [fecha, setFecha] = useState<Date | null>(new Date());
    const [horas, setHoras] = useState<number | ''>('');
    const [proyectoCodigo, setProyectoCodigo] = useState<string>('');
    const [etapa, setEtapa] = useState<string>('');
    const [disciplina, setDisciplina] = useState<string>('');
    const [actividad, setActividad] = useState<string>('');
    const [descripcion, setDescripcion] = useState<string>('');
    const [empleado, setEmpleado] = useState<string>(''); // Estado para el empleado
    const [proyectos, setProyectos] = useState<any[]>([]);
    const [etapas, setEtapas] = useState<string[]>([]);
    const [disciplinas, setDisciplinas] = useState<string[]>([]);
    const [actividades, setActividades] = useState<string[]>([]);
    const [employees, setEmployees] = useState<Employee[]>([]); // Estado para los empleados

    const API_BASE_URL = import.meta.env.VITE_API_URL || 'http://localhost:8000';


    // Fetch employees
    useEffect(() => {
        const fetchEmployees = async () => {
            try {
                const response = await axios.get(`${API_BASE_URL}/employees/`);
                console.log("Employees fetched:", response.data); // Debugging: log the fetched data
                setEmployees(response.data);
            } catch (error) {
                console.error("Error fetching employees:", error);
            }
        };
        fetchEmployees();
    }, [API_BASE_URL]);


    useEffect(() => {
        const fetchProyectos = async () => {
            try {
                const response = await axios.get(`${API_BASE_URL}/projects/`);
                setProyectos(response.data);
            } catch (error) {
                console.error("Error fetching projects:", error);
            }
        };
        fetchProyectos();
    }, [API_BASE_URL]);

    useEffect(() => {
        if (proyectoCodigo) {
            const fetchEtapas = async () => {
                try {
                    const response = await axios.get(`${API_BASE_URL}/projects/${proyectoCodigo}/stages`);
                    setEtapas(response.data);
                } catch (error) {
                    console.error("Error fetching stages:", error);
                    setEtapas([]);
                }
            };
            fetchEtapas();
        } else {
            setEtapas([]);
        }
    }, [proyectoCodigo, API_BASE_URL]);

    useEffect(() => {
        if (proyectoCodigo && etapa) {
            const fetchDisciplinas = async () => {
                try {
                    const response = await axios.get(`${API_BASE_URL}/activities/${proyectoCodigo}/${etapa}/disciplines`);
                    setDisciplinas(response.data);
                } catch (error) {
                    console.error("Error fetching disciplines:", error);
                    setDisciplinas([]);
                }
            };
            fetchDisciplinas();
        } else {
            setDisciplinas([]);
        }
    }, [proyectoCodigo, etapa, API_BASE_URL]);

    useEffect(() => {
        if (proyectoCodigo && etapa && disciplina) {
            const fetchActividades = async () => {
                try {
                    const response = await axios.get(`${API_BASE_URL}/activities/${proyectoCodigo}/${etapa}/${disciplina}/activities`);
                    setActividades(response.data);
                } catch (error) {
                    console.error("Error fetching activities:", error);
                    setActividades([]);
                }
            };
            fetchActividades();
        } else {
            setActividades([]);
        }
    }, [proyectoCodigo, etapa, disciplina, API_BASE_URL]);


    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        // Lógica para enviar los datos a tu backend
        const datosReporte = {
            date: fecha?.toISOString().split('T')[0],
            hours: horas,
            project_code: proyectoCodigo,
            stage: etapa,
            discipline: disciplina,
            activity: actividad,
            description: descripcion,
            employee_id: empleado // Asegúrate de que este sea el ID del empleado
        };

        try {
            await axios.post(`${API_BASE_URL}/hours/`, datosReporte);
            alert('Horas reportadas con éxito');
            onSuccess();
            // Limpiar el formulario o restablecer estados
            setFecha(new Date());
            setHoras('');
            setProyectoCodigo('');
            setEtapa('');
            setDisciplina('');
            setActividad('');
            setDescripcion('');
            setEmpleado('');
        } catch (error) {
            console.error('Error al reportar horas:', error);
            alert('Error al reportar horas. Consulta la consola para más detalles.');
        }
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-4 p-4 bg-white rounded shadow-md">
            <h2 className="text-2xl font-bold mb-4">Reporte de Horas</h2>

            <DatePicker label="Fecha" selectedDate={fecha} onChange={setFecha} />

            <InputField
                label="Horas"
                type="number"
                value={horas}
                onChange={(e) => setHoras(parseFloat(e.target.value) || '')}
                placeholder="Ej: 8"
            />

            <SelectField
                label="Empleado"
                value={empleado}
                onChange={(e) => setEmpleado(e.target.value)}
                options={employees.map(emp => ({ value: emp.id, label: emp.short_name }))}
                placeholder="Seleccione un empleado"
            />

            <SelectField
                label="Proyecto"
                value={proyectoCodigo}
                onChange={(e) => { setProyectoCodigo(e.target.value); setEtapa(''); setDisciplina(''); setActividad(''); }}
                options={proyectos.map(p => ({ value: p.code, label: p.name }))}
                placeholder="Seleccione un proyecto"
            />

            {proyectoCodigo && (
                <SelectField
                    label="Etapa"
                    value={etapa}
                    onChange={(e) => { setEtapa(e.target.value); setDisciplina(''); setActividad(''); }}
                    options={etapas.map(s => ({ value: s, label: s }))}
                    placeholder="Seleccione una etapa"
                />
            )}

            {etapa && (
                <SelectField
                    label="Disciplina"
                    value={disciplina}
                    onChange={(e) => { setDisciplina(e.target.value); setActividad(''); }}
                    options={disciplinas.map(d => ({ value: d, label: d }))}
                    placeholder="Seleccione una disciplina"
                />
            )}

            {disciplina && (
                <SelectField
                    label="Actividad"
                    value={actividad}
                    onChange={(e) => setActividad(e.target.value)}
                    options={actividades.map(a => ({ value: a, label: a }))}
                    placeholder="Seleccione una actividad"
                />
            )}


            <TextareaField
                label="Descripción"
                value={descripcion}
                onChange={(e) => setDescripcion(e.target.value)}
                placeholder="Descripción detallada de la actividad realizada"
            />

            <button
                type="submit"
                className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
            >
                Reportar Horas
            </button>

            <ActividadesList />
        </form>
    );
};

export default FormularioHoras;
