import React, { useState, useEffect } from 'react';
import InputField from './InputField';
import SelectField from './SelectField';
import TextareaField from './TextareaField';
import DatePicker from './DatePicker';
import { 
  submitHours, 
  getProjects, 
  getEmployees, 
  getProjectStages, 
  getDisciplinesByStage, 
  getActivitiesByDiscipline,
  getDailyActivities
} from '../api/horasApi';

interface FormularioHorasProps {
    onSuccess: () => void;
}

interface Project {
    code: string;
    name: string;
}

interface Employee {
    id: number;
    name: string;
}

interface Activity {
    id: number;
    date: string;
    employee_id: number;
    project_code: string;
    project_name: string;
    phase: string;
    discipline: string;
    activity: string;
    hours: number;
    note: string;
}

const FormularioHoras: React.FC<FormularioHorasProps> = ({ onSuccess }) => {
    // Estados para manejar errores y carga
    const [submitError, setSubmitError] = useState<string | null>(null);
    
    // Estados para fecha y empleado
    const [selectedDate, setSelectedDate] = useState<Date | null>(new Date());
    const [formattedDate, setFormattedDate] = useState<string>('');
    const [employees, setEmployees] = useState<Employee[]>([]);
    const [loadingEmployees, setLoadingEmployees] = useState<boolean>(true);
    const [selectedEmployeeName, setSelectedEmployeeName] = useState<string>('');
    
    // Estado para datos del formulario
    const [formData, setFormData] = useState({
        employee_id: '',
        project_code: '',
        phase: '',
        discipline: '',
        activity: '',
        hours: 0,
        note: ''
    });
    
    // Estados para proyectos
    const [projects, setProjects] = useState<Project[]>([]);
    const [loadingProjects, setLoadingProjects] = useState<boolean>(true);
    const [selectedProjectName, setSelectedProjectName] = useState<string>('');
    
    // Estados para etapas, disciplinas y actividades
    const [stages, setStages] = useState<string[]>([]);
    const [loadingStages, setLoadingStages] = useState<boolean>(false);
    const [disciplines, setDisciplines] = useState<string[]>([]);
    const [loadingDisciplines, setLoadingDisciplines] = useState<boolean>(false);
    const [activities, setActivities] = useState<string[]>([]);
    const [loadingActivities, setLoadingActivities] = useState<boolean>(false);
    
    // Estados para actividades diarias
    const [dailyActivities, setDailyActivities] = useState<Activity[]>([]);
    const [loadingDailyActivities, setLoadingDailyActivities] = useState<boolean>(true);

    // Formatear fecha seleccionada
    useEffect(() => {
        if (selectedDate) {
            const formatted = selectedDate.toLocaleDateString('es-ES', {
                weekday: 'long',
                year: 'numeric',
                month: 'long',
                day: 'numeric'
            });
            setFormattedDate(formatted);
        }
    }, [selectedDate]);

    // Obtener empleados
    useEffect(() => {
        const fetchEmployees = async () => {
            try {
                const data = await getEmployees();
                setEmployees(data);
            } catch (error) {
                console.error('Error fetching employees:', error);
            } finally {
                setLoadingEmployees(false);
            }
        };
        fetchEmployees();
    }, []);

    // Obtener proyectos
    useEffect(() => {
        const fetchProjects = async () => {
            try {
                const data = await getProjects();
                setProjects(data);
            } catch (error) {
                console.error('Error fetching projects:', error);
            } finally {
                setLoadingProjects(false);
            }
        };
        fetchProjects();
    }, []);

    // Obtener etapas del proyecto
    useEffect(() => {
        if (formData.project_code) {
            setLoadingStages(true);
            getProjectStages(formData.project_code)
                .then(data => {
                    setStages(data);
                })
                .catch(error => {
                    console.error('Error fetching stages:', error);
                })
                .finally(() => {
                    setLoadingStages(false);
                });
        } else {
            setStages([]);
            setFormData(prev => ({ ...prev, phase: '', discipline: '', activity: '' }));
        }
    }, [formData.project_code]);

    // Obtener disciplinas
    useEffect(() => {
        if (formData.project_code && formData.phase) {
            setLoadingDisciplines(true);
            getDisciplinesByStage(formData.project_code, formData.phase)
                .then(data => {
                    setDisciplines(data);
                })
                .catch(error => {
                    console.error('Error fetching disciplines:', error);
                })
                .finally(() => {
                    setLoadingDisciplines(false);
                });
        } else {
            setDisciplines([]);
            setFormData(prev => ({ ...prev, discipline: '', activity: '' }));
        }
    }, [formData.project_code, formData.phase]);

    // Obtener actividades
    useEffect(() => {
        if (formData.project_code && formData.phase && formData.discipline) {
            setLoadingActivities(true);
            getActivitiesByDiscipline(formData.project_code, formData.phase, formData.discipline)
                .then(data => {
                    setActivities(data);
                })
                .catch(error => {
                    console.error('Error fetching activities:', error);
                })
                .finally(() => {
                    setLoadingActivities(false);
                });
        } else {
            setActivities([]);
            setFormData(prev => ({ ...prev, activity: '' }));
        }
    }, [formData.project_code, formData.phase, formData.discipline]);

    // Obtener actividades diarias
    useEffect(() => {
        if (selectedDate && formData.employee_id) {
            setLoadingDailyActivities(true);
            const dateString = selectedDate.toISOString().split('T')[0];
            getDailyActivities(dateString, parseInt(formData.employee_id))
                .then(data => {
                    setDailyActivities(data);
                })
                .catch(error => {
                    console.error('Error fetching daily activities:', error);
                })
                .finally(() => {
                    setLoadingDailyActivities(false);
                });
        } else {
            setDailyActivities([]);
            setLoadingDailyActivities(false);
        }
    }, [selectedDate, formData.employee_id]);

    // Manejador de cambio de fecha
    const handleDateChange = (date: Date | null) => {
        setSelectedDate(date);
    };

    // Manejador de cambios generales en el formulario
    const handleChange = (
        e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>
    ) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
        
        if (name === 'employee_id') {
            const employee = employees.find(emp => emp.id === parseInt(value));
            setSelectedEmployeeName(employee ? employee.name : '');
        }
    };

    // Manejador específico para cambio de proyecto
    const handleProjectCodeChange = (
        e: React.ChangeEvent<HTMLSelectElement>
    ) => {
        const value = e.target.value;
        setFormData(prev => ({ 
            ...prev, 
            project_code: value,
            phase: '',
            discipline: '',
            activity: ''
        }));
        
        const project = projects.find(proj => proj.code === value);
        setSelectedProjectName(project ? project.name : '');
    };

    // Manejador de envío del formulario
    const handleSubmit = async () => {
        if (!selectedDate) {
            setSubmitError('Por favor seleccione una fecha');
            return;
        }
        
        if (!formData.employee_id) {
            setSubmitError('Por favor seleccione un colaborador');
            return;
        }
        
        if (!formData.project_code) {
            setSubmitError('Por favor seleccione un proyecto');
            return;
        }
        
        if (!formData.phase) {
            setSubmitError('Por favor seleccione una etapa');
            return;
        }
        
        if (!formData.discipline) {
            setSubmitError('Por favor seleccione una disciplina');
            return;
        }
        
        if (!formData.activity) {
            setSubmitError('Por favor seleccione una actividad');
            return;
        }
        
        if (!formData.hours || formData.hours <= 0) {
            setSubmitError('Por favor ingrese un número válido de horas');
            return;
        }
        
        try {
            await submitHours({
                ...formData,
                date: selectedDate.toISOString().split('T')[0],
                hours: parseFloat(formData.hours.toString())
            });
            setSubmitError(null);
            onSuccess();
            
            // Resetear campos dependientes del proyecto, pero mantener empleado y proyecto
            setFormData(prev => ({
                ...prev,
                phase: '',
                discipline: '',
                activity: '',
                hours: 0,
                note: ''
            }));
            
            // Recargar las actividades diarias
            const dateString = selectedDate.toISOString().split('T')[0];
            const newActivities = await getDailyActivities(dateString, parseInt(formData.employee_id));
            setDailyActivities(newActivities);
        } catch (error) {
            setSubmitError('Error al registrar las horas');
            console.error('Error submitting hours:', error);
        }
    };

    return (
        <div className="max-w-7xl mx-auto p-6">
            <div className="text-center mb-8">
                <h1 className="text-7xl font-bold text-[#083c4c]">Ingenio BIM</h1>
                <p className="text-[#083c4c] font-medium text-xl">CONSTRUCCIÓN INTELIGENTE</p>
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                {/* Formulario */}
                <div className="lg:col-span-2">
                    <div className="bg-white rounded-xl shadow-lg overflow-hidden border border-[#083c4c]">
                        <div className="bg-[#083c4c] text-white p-4">
                            <h2 className="text-xl font-bold text-center">REGISTRO DE HORAS</h2>
                            <p className="text-center text-[#FBE205] text-sm">Complete el formulario para registrar sus horas de trabajo</p>
                        </div>

                        <div className="p-6">
                            {submitError && (
                                <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-6" role="alert">
                                    {submitError}
                                </div>
                            )}

                            {/* Sección de Fecha y Empleado */}
                            <div className="mb-6">
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700 mb-2">FECHA</label>
                                        <DatePicker
                                            label=""
                                            selectedDate={selectedDate}
                                            onChange={handleDateChange}
                                        />
                                    </div>
                                    
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700 mb-2">COLABORADOR</label>
                                        <SelectField
                                            label=""
                                            name="employee_id"
                                            value={formData.employee_id}
                                            onChange={handleChange}
                                            options={employees.map(emp => ({ value: emp.id.toString(), label: emp.name }))}
                                            disabled={loadingEmployees}
                                            isLoading={loadingEmployees}
                                        />
                                    </div>
                                </div>

                                <div className="bg-[#083c4c] rounded-lg p-4 border border-gray-200 text-white">
                                    <div className="flex items-center mb-2">
                                        <div className="text-[#FBE205] font-medium">COLABORADOR •</div>
                                        <div className="ml-2 font-bold">{selectedEmployeeName || 'Seleccione un colaborador'}</div>
                                    </div>
                                    <div className="text-white font-bold">{formattedDate}</div>
                                </div>
                            </div>

                            {/* Sección de Proyecto */}
                            <div className="mb-6">
                                <div className="bg-[#083c4c] rounded-lg p-4 border border-gray-200 mb-4 text-white">
                                    <div className="flex items-center">
                                        <div className="text-[#FBE205] font-medium">PROYECTO •</div>
                                        <div className="ml-2 font-bold">{selectedProjectName || 'Seleccione un proyecto'}</div>
                                    </div>
                                </div>

                                <div className="grid grid-cols-1 gap-6">
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700 mb-2">CÓDIGO</label>
                                        <SelectField
                                            label=""
                                            name="project_code"
                                            value={formData.project_code}
                                            onChange={handleProjectCodeChange}
                                            options={projects.map(proj => ({ value: proj.code, label: proj.code }))}
                                            disabled={loadingProjects}
                                            isLoading={loadingProjects}
                                        />
                                    </div>
                                </div>
                            </div>

                            {/* Sección de Etapa, Disciplina y Actividad */}
                            <div className="mb-6">
                                <div className="grid grid-cols-1 gap-6">
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700 mb-2">ETAPA</label>
                                        <SelectField
                                            label=""
                                            name="phase"
                                            value={formData.phase}
                                            onChange={handleChange}
                                            options={stages.map(stage => ({ value: stage, label: stage }))}
                                            disabled={!formData.project_code || loadingStages}
                                            isLoading={loadingStages}
                                        />
                                    </div>
                                    
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700 mb-2">DISCIPLINA</label>
                                        <SelectField
                                            label=""
                                            name="discipline"
                                            value={formData.discipline}
                                            onChange={handleChange}
                                            options={disciplines.map(disc => ({ value: disc, label: disc }))}
                                            disabled={!formData.phase || loadingDisciplines}
                                            isLoading={loadingDisciplines}
                                        />
                                    </div>
                                    
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700 mb-2">ACTIVIDAD</label>
                                        <SelectField
                                            label=""
                                            name="activity"
                                            value={formData.activity}
                                            onChange={handleChange}
                                            options={activities.map(act => ({ value: act, label: act }))}
                                            disabled={!formData.discipline || loadingActivities}
                                            isLoading={loadingActivities}
                                        />
                                    </div>
                                </div>
                            </div>

                            {/* Sección de Horas y Nota */}
                            <div className="mb-8">
                                <div className="grid grid-cols-1 gap-6">
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700 mb-2">HORAS</label>
                                        <InputField
                                            label=""
                                            name="hours"
                                            type="number"
                                            value={formData.hours}
                                            onChange={handleChange}
                                            min="0.5"
                                            step="0.5"
                                        />
                                    </div>
                                    
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700 mb-2">NOTA (OPCIONAL)</label>
                                        <TextareaField
                                            label=""
                                            name="note"
                                            value={formData.note}
                                            onChange={handleChange}
                                            rows={3}
                                        />
                                    </div>
                                </div>
                            </div>

                            {/* Botones */}
                            <div className="flex justify-between">
                                <button
                                    type="button"
                                    className="bg-gray-500 hover:bg-gray-600 text-white font-bold py-2 px-6 rounded-lg transition duration-200"
                                >
                                    CANCELAR
                                </button>
                                <button
                                    type="submit"
                                    className="bg-[#FBE205] hover:bg-[#e0c900] text-[#083c4c] font-bold py-2 px-6 rounded-lg transition duration-200"
                                    onClick={handleSubmit}
                                >
                                    REGISTRAR
                                </button>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Listado de Actividades Registradas */}
                <div>
                    <div className="bg-white rounded-xl shadow-lg overflow-hidden border border-[#083c4c] h-full">
                        <div className="bg-[#083c4c] text-white p-4">
                            <h2 className="text-xl font-bold text-center">LISTADO DE ACTIVIDADES</h2>
                        </div>
                        
                        <div className="p-4 overflow-y-auto max-h-[calc(100vh-200px)]">
                            {loadingDailyActivities ? (
                                <div className="text-center py-8">
                                    <div className="animate-spin rounded-full h-10 w-10 border-b-2 border-[#083c4c] mx-auto"></div>
                                    <p className="mt-3 text-gray-600">Cargando actividades...</p>
                                </div>
                            ) : dailyActivities.length === 0 ? (
                                <div className="text-center py-8 bg-gray-50 rounded-lg">
                                    <div className="text-gray-400 mb-3">
                                        <svg xmlns="http://www.w3.org/2000/svg" className="h-16 w-16 mx-auto" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-3 7h3m-3 4h3m-6-4h.01M9 16h.01" />
                                        </svg>
                                    </div>
                                    <p className="text-gray-500">No hay actividades registradas para este día</p>
                                </div>
                            ) : (
                                <div className="space-y-4">
                                    {dailyActivities.map((activity, index) => (
                                        <div key={index} className="bg-white border rounded-lg p-4 shadow-sm border-[#083c4c]">
                                            <div className="flex justify-between items-start mb-3">
                                                <h4 className="font-bold text-lg text-[#083c4c]">{activity.activity}</h4>
                                                <span className="text-sm text-gray-500">{activity.date}</span>
                                            </div>
                                            
                                            <div className="text-sm text-gray-700">
                                                <p className="font-semibold mb-1">Proyecto {activity.project_name}</p>
                                                
                                                <div className="grid grid-cols-3 gap-2 mt-3">
                                                    <div>
                                                        <div className="text-gray-500 text-xs">ETAPA</div>
                                                        <div>{activity.phase}</div>
                                                    </div>
                                                    
                                                    <div>
                                                        <div className="text-gray-500 text-xs">DISCIPLINA</div>
                                                        <div>{activity.discipline}</div>
                                                    </div>
                                                    
                                                    <div>
                                                        <div className="text-gray-500 text-xs">HORAS</div>
                                                        <div className="font-bold text-[#083c4c]">{activity.hours} hrs</div>
                                                    </div>
                                                </div>
                                                
                                                {activity.note && (
                                                    <div className="mt-4 pt-3 border-t border-gray-100">
                                                        <div className="text-gray-500 text-xs">NOTA</div>
                                                        <div>{activity.note}</div>
                                                    </div>
                                                )}
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default FormularioHoras;
