import React from 'react';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '../ui/select';
import { Loader2 } from 'lucide-react';

interface FormSelectProps {
    label: string;
    name: string; // <-- AÃ±adido
    value: string;
    onValueChange: (name: string, value: string) => void; // <-- Modificado
    options: { value: string; label: string }[];
    placeholder: string;
    loading: boolean;
    disabled: boolean;
    icon: React.ReactNode;
    required?: boolean;
}

export const FormSelect: React.FC<FormSelectProps> = ({ label, name, value, onValueChange, options, placeholder, loading, disabled, icon, required }) => (
    <div className="space-y-2">
        <label htmlFor={name} className="text-sm font-medium flex items-center">
            {icon}
            <span className="ml-2">{label}{required && <span className="text-destructive ml-1">*</span>}</span>
            {loading && <Loader2 className="ml-2 h-4 w-4 animate-spin" />}
        </label>
        <Select onValueChange={(val) => onValueChange(name, val)} value={value} disabled={disabled || loading}>
            <SelectTrigger id={name}>
                <SelectValue placeholder={placeholder} />
            </SelectTrigger>
            <SelectContent>
                {options.map(option => (
                    <SelectItem key={option.value} value={option.value}>
                        {option.label}
                    </SelectItem>
                ))}
            </SelectContent>
        </Select>
    </div>
);
