import React from 'react';
import { format } from 'date-fns';
import { DayPicker } from 'react-day-picker';
import 'react-day-picker/dist/style.css';

interface DatePickerProps {
    label: string;
    selectedDate: Date | null;
    onChange: (date: Date | null) => void;
    required?: boolean;
}

const DatePicker: React.FC<DatePickerProps> = ({ 
    label, 
    selectedDate, 
    onChange, 
    required = false 
}) => {
    const [isOpen, setIsOpen] = React.useState(false);

    const handleSelect = (date?: Date) => {
        onChange(date || null); // Maneja undefined (selección vacía)
        setIsOpen(false);
    };

    return (
        <div className="mb-4 relative">
            <label className="block text-gray-700 text-sm font-bold mb-2">
                {label} {required && <span className="text-red-500">*</span>}
            </label>
            <div className="relative">
                <input
                    type="text"
                    readOnly
                    value={selectedDate ? format(selectedDate, 'yyyy-MM-dd') : ''}
                    onClick={() => setIsOpen(!isOpen)}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline cursor-pointer"
                />
                {isOpen && (
                    <div className="absolute z-10 mt-1 bg-white border border-gray-300 rounded shadow-lg">
                        <DayPicker
                            mode="single"
                            selected={selectedDate || undefined}
                            onSelect={handleSelect}
                        />
                    </div>
                )}
            </div>
        </div>
    );
};

export default DatePicker;
