import React, { useState, useEffect, useRef, ChangeEvent } from 'react';
import InputField from './InputField';
import SelectField from './SelectField';
import TextareaField from './TextareaField';
import DatePicker from './DatePicker';
import { 
  submitHours, 
  getProjects, 
  getEmployees, 
  getProjectStages, 
  getDisciplinesByStage, 
  getActivitiesByDiscipline,
  getDailyActivities,
  getProjectByCode,
  updateHour,
  deleteHour
} from '../api/horasApi';

// Interfaces
interface FormState {
    date: string;
    employee_id: string;
    project_code: string;
    phase: string;
    discipline: string;
    activity: string;
    hours: string;
    note: string;
}

interface Employee {
    id: number;
    name: string;
}

interface Project {
    code: string;
    name: string;
}

interface Activity {
    id: number;
    date: string;
    employee_id: number;
    project_code: string;
    project_name: string;
    phase: string;
    discipline: string;
    activity: string;
    hours: number;
    note?: string;
}

interface FormularioHorasProps {
    onSuccess: () => void;
}

const initialFormState: FormState = {
    date: '',
    employee_id: '',
    project_code: '',
    phase: '',
    discipline: '',
    activity: '',
    hours: '',
    note: '',
};

const FormularioHoras: React.FC<FormularioHorasProps> = ({ onSuccess }) => {
    // State declarations
    const [loadingDailyActivities, setLoadingDailyActivities] = useState<boolean>(true);
    const [dailyActivities, setDailyActivities] = useState<Activity[]>([]);
    const [formData, setFormData] = useState<FormState>(initialFormState);
    const [selectedProjectName, setSelectedProjectName] = useState<string>('');
    const [employees, setEmployees] = useState<Employee[]>([]);
    const [loadingEmployees, setLoadingEmployees] = useState<boolean>(true);
    const [stages, setStages] = useState<string[]>([]);
    const [loadingStages, setLoadingStages] = useState<boolean>(false);
    const [disciplines, setDisciplines] = useState<string[]>([]);
    const [loadingDisciplines, setLoadingDisciplines] = useState<boolean>(false);
    const [activities, setActivities] = useState<string[]>([]);
    const [loadingActivities, setLoadingActivities] = useState<boolean>(false);
    const [editingActivityId, setEditingActivityId] = useState<number | null>(null);
    const [selectedEmployeeName, setSelectedEmployeeName] = useState<string>('');
    const [formattedDate, setFormattedDate] = useState<string>('');

    // Refs
    const formRef = useRef<HTMLDivElement>(null);
    const [submitError, setSubmitError] = useState<string | null>(null);
    const [selectedDate, setSelectedDate] = useState<Date | null>(new Date());
    const [projects, setProjects] = useState<Project[]>([]);
    const [loadingProjects, setLoadingProjects] = useState<boolean>(true);

    // Formatear fecha para mostrar
    useEffect(() => {
        if (selectedDate) {
            const options: Intl.DateTimeFormatOptions = { year: 'numeric', month: 'long', day: 'numeric' };
            setFormattedDate(selectedDate.toLocaleDateString('es-ES', options));
        }
    }, [selectedDate]);

    // Cargar empleados y proyectos al montar
    useEffect(() => {
        const fetchInitialData = async () => {
            try {
                const [employeesData, projectsData] = await Promise.all([
                    getEmployees(),
                    getProjects()
                ]);
                setEmployees(employeesData);
                setProjects(projectsData);
            } catch (error) {
                console.error('Error fetching initial data:', error);
            } finally {
                setLoadingEmployees(false);
                setLoadingProjects(false);
            }
        };
        fetchInitialData();
    }, []);

    // Cargar actividades diarias cuando cambia la fecha o el empleado
    useEffect(() => {
        if (selectedDate && formData.employee_id) {
            refreshDailyActivities();
        } else {
            setDailyActivities([]);
        }
    }, [selectedDate, formData.employee_id]);

    // Cargar etapas cuando cambia el código de proyecto
    useEffect(() => {
        const fetchStages = async () => {
            if (formData.project_code) {
                setLoadingStages(true);
                try {
                    const stagesData = await getProjectStages(formData.project_code);
                    setStages(stagesData);
                } catch (error) {
                    console.error('Error fetching stages:', error);
                    setStages([]);
                } finally {
                    setLoadingStages(false);
                }
            } else {
                setStages([]);
            }
        };
        fetchStages();
    }, [formData.project_code]);

    // Cargar disciplinas cuando cambia la etapa
    useEffect(() => {
        const fetchDisciplines = async () => {
            if (formData.phase) {
                setLoadingDisciplines(true);
                try {
                    const disciplinesData = await getDisciplinesByStage(formData.project_code, formData.phase);
                    setDisciplines(disciplinesData);
                } catch (error) {
                    console.error('Error fetching disciplines:', error);
                    setDisciplines([]);
                } finally {
                    setLoadingDisciplines(false);
                }
            } else {
                setDisciplines([]);
            }
        };
        fetchDisciplines();
    }, [formData.phase, formData.project_code]);

    // Cargar actividades cuando cambia la disciplina
    useEffect(() => {
        const fetchActivities = async () => {
            if (formData.discipline) {
                setLoadingActivities(true);
                try {
                    const activitiesData = await getActivitiesByDiscipline(formData.project_code, formData.phase, formData.discipline);
                    setActivities(activitiesData);
                } catch (error) {
                    console.error('Error fetching activities:', error);
                    setActivities([]);
                } finally {
                    setLoadingActivities(false);
                }
            } else {
                setActivities([]);
            }
        };
        fetchActivities();
    }, [formData.discipline, formData.phase, formData.project_code]);

    // Handlers
    const refreshDailyActivities = async () => {
        if (!selectedDate || !formData.employee_id) return;

        setLoadingDailyActivities(true);
        try {
            const dateStr = selectedDate.toISOString().split('T')[0];
            const activities = await getDailyActivities(dateStr, Number(formData.employee_id));
            setDailyActivities(activities);
        } catch (error) {
            console.error('Error fetching daily activities:', error);
            setDailyActivities([]);
        } finally {
            setLoadingDailyActivities(false);
        }
    };

    const handleDateChange = (date: Date | null) => {
        setSelectedDate(date);
    };

    const handleChange = (e: ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
        if (name === 'employee_id') {
            const employee = employees.find(emp => emp.id === parseInt(value, 10));
            setSelectedEmployeeName(employee ? employee.name : '');
        }
    };

    const handleProjectCodeChange = async (e: ChangeEvent<HTMLSelectElement>) => {
        const projectCode = e.target.value;
        setFormData(prev => ({ ...prev, project_code: projectCode, phase: '', discipline: '', activity: '' }));
        if (projectCode) {
            try {
                const projectDetails = await getProjectByCode(projectCode);
                setSelectedProjectName(projectDetails.name);
            } catch (error) {
                console.error('Error fetching project details:', error);
                setSelectedProjectName('');
            }
        } else {
            setSelectedProjectName('');
        }
    };

    const handleSelectActivity = (activity: Activity) => {
        setFormData({
            date: activity.date,
            employee_id: String(activity.employee_id),
            project_code: activity.project_code,
            phase: activity.phase,
            discipline: activity.discipline,
            activity: activity.activity,
            hours: String(activity.hours),
            note: activity.note || '',
        });
        setEditingActivityId(activity.id);
        if (formRef.current) {
            formRef.current.scrollIntoView({ behavior: 'smooth' });
        }
    };

    const resetForm = () => {
        setFormData(prev => ({ ...initialFormState, employee_id: prev.employee_id, date: prev.date }));
        setEditingActivityId(null);
        setSelectedProjectName('');
        setSubmitError(null);
        setStages([]);
        setDisciplines([]);
        setActivities([]);
    };

    const handleSubmit = async () => {
        if (!formData.employee_id || !formData.project_code || !formData.hours) {
            setSubmitError('Por favor, complete todos los campos obligatorios.');
            return;
        }
        setSubmitError(null);

        const data = {
            ...formData,
            date: selectedDate ? selectedDate.toISOString().split('T')[0] : '',
            employee_id: parseInt(formData.employee_id, 10),
            hours: parseFloat(formData.hours),
        };

        try {
            if (editingActivityId) {
                await updateHour(editingActivityId, data);
            } else {
                await submitHours(data);
            }
            resetForm();
            refreshDailyActivities();
            onSuccess();
        } catch (error) {
            console.error('Error submitting form:', error);
            setSubmitError('Ocurrió un error al guardar los datos.');
        }
    };

    const handleDelete = async () => {
        if (!editingActivityId) return;

        try {
            await deleteHour(editingActivityId);
            resetForm();
            refreshDailyActivities();
            onSuccess();
        } catch (error) {
            console.error('Error deleting activity:', error);
            setSubmitError('Ocurrió un error al eliminar la actividad.');
        }
    };

    return (
        <div className="p-6 bg-gray-50 min-h-screen">
            {/* Contenedor principal con dos columnas */}
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">

                {/* Columna Izquierda: Formulario */}
                <div className="lg:col-span-2 bg-white p-8 rounded-2xl shadow-lg border border-gray-200" ref={formRef}>
                    <h2 className="text-3xl font-bold text-[#083c4c] mb-2">Registro de Horas</h2>
                    <p className="text-gray-500 mb-8">Completa los detalles de la actividad</p>

                    {submitError && (
                        <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mb-6 rounded-md" role="alert">
                            <p className="font-bold">Error</p>
                            <p>{submitError}</p>
                        </div>
                    )}

                    <form onSubmit={(e) => { e.preventDefault(); handleSubmit(); }} className="space-y-6">
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                            <DatePicker 
                                selectedDate={selectedDate} 
                                onChange={handleDateChange} 
                                label="FECHA" 
                                required 
                            />
                            <div>
                                <SelectField
                                    label="EMPLEADO"
                                    name="employee_id"
                                    value={String(formData.employee_id)}
                                    onChange={handleChange}
                                    options={employees.map(e => ({ value: String(e.id), label: e.name }))}
                                    isLoading={loadingEmployees}
                                    required
                                />
                            </div>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                            <div>
                                <SelectField
                                    label="CÓDIGO DE PROYECTO"
                                    name="project_code"
                                    value={formData.project_code}
                                    onChange={handleProjectCodeChange}
                                    options={projects.map(p => ({ value: p.code, label: `${p.code} - ${p.name}` }))}
                                    isLoading={loadingProjects}
                                    required
                                />
                            </div>
                        </div>

                        {/* Fila para Etapa, Disciplina, Actividad y Horas */}
                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                            <SelectField
                                label="ETAPA"
                                name="phase"
                                value={formData.phase}
                                onChange={handleChange}
                                options={stages.map(s => ({ value: s, label: s }))}
                                disabled={!formData.project_code || loadingStages}
                                isLoading={loadingStages}
                                required
                            />
                            <SelectField
                                label="DISCIPLINA"
                                name="discipline"
                                value={formData.discipline}
                                onChange={handleChange}
                                options={disciplines.map(d => ({ value: d, label: d }))}
                                disabled={!formData.phase || loadingDisciplines}
                                isLoading={loadingDisciplines}
                                required
                            />
                            <SelectField
                                label="ACTIVIDAD"
                                name="activity"
                                value={formData.activity}
                                onChange={handleChange}
                                options={activities.map(a => ({ value: a, label: a }))}
                                disabled={!formData.discipline || loadingActivities}
                                isLoading={loadingActivities}
                                required
                            />
                            <InputField
                                label="HORAS"
                                name="hours"
                                type="number"
                                value={formData.hours}
                                onChange={handleChange}
                                required
                            />
                        </div>

                        {/* Fila para Nota */}
                        <TextareaField
                            label="NOTA"
                            name="note"
                            value={formData.note}
                            onChange={handleChange}
                        />

                        {/* Botones de acción */}
                        <div className="flex items-center justify-end space-x-4 pt-4">
                            <button
                                type="button"
                                onClick={resetForm}
                                className="px-6 py-3 rounded-lg font-semibold text-gray-600 bg-gray-100 hover:bg-gray-200 transition-colors duration-200"
                            >
                                {editingActivityId ? 'Cancelar Edición' : 'Limpiar'}
                            </button>
                            <button
                                type="submit"
                                className="px-6 py-3 rounded-lg font-semibold text-white bg-[#083c4c] hover:bg-[#062e3a] transition-colors duration-200 shadow-md"
                            >
                                {editingActivityId ? 'Actualizar Actividad' : 'Registrar Horas'}
                            </button>
                            {editingActivityId && (
                                <button
                                    type="button"
                                    onClick={handleDelete}
                                    className="bg-red-600 text-white py-3 px-4 rounded-lg hover:bg-red-700 transition-colors duration-200 shadow-md"
                                >
                                    Eliminar
                                </button>
                            )}
                        </div>
          </form>
        </div>

        <div className="lg:col-span-1">
          <div className="bg-white p-8 rounded-2xl shadow-lg border border-gray-200 h-full">
            <h3 className="text-2xl font-bold text-[#083c4c] mb-2">Actividades del Día</h3>
            <p className="text-gray-500 mb-6">
              {selectedEmployeeName ? `${selectedEmployeeName} - ${selectedDate?.toLocaleDateString()}` : `Seleccione un empleado`}
            </p>

            <div className="h-[600px] overflow-y-auto pr-4 -mr-4 custom-scrollbar">
              {loadingDailyActivities ? (
                <div className="flex items-center justify-center h-full">
                  <div className="w-16 h-16 border-t-4 border-b-4 border-[#083c4c] rounded-full animate-spin"></div>
                </div>
              ) : dailyActivities.length === 0 ? (
                <div className="text-center py-16">
                  <div className="text-gray-400 mb-4">
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      className="h-16 w-16 mx-auto"
                      fill="none"
                      viewBox="0 0 24 24"
                      stroke="currentColor"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={1.5}
                        d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-3 7h3m-3 4h3m-6-4h.01M9 16h.01"
                      />
                    </svg>
                  </div>
                  <p className="text-gray-500">No hay actividades registradas para este día</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {dailyActivities.map((activity, index) => (
                    <div
                      key={index}
                      className={`bg-white border rounded-lg p-4 shadow-sm border-[#083c4c] cursor-pointer hover:bg-gray-50 transition-colors duration-200 ${
                        editingActivityId === activity.id ? 'ring-2 ring-yellow-400' : ''
                      }`}
                      onClick={() => handleSelectActivity(activity)}
                    >
                      <div className="flex justify-between items-start mb-3">
                        <h4 className="font-bold text-lg text-[#083c4c]">{activity.activity}</h4>
                        <span className="text-sm text-gray-500">{activity.date}</span>
                      </div>

                      <div className="text-sm text-gray-700">
                        <p className="font-semibold mb-1">Proyecto {activity.project_name}</p>

                        <div className="grid grid-cols-3 gap-2 mt-3">
                          <div>
                            <div className="text-gray-500 text-xs">ETAPA</div>
                            <div>{activity.phase}</div>
                          </div>

                          <div>
                            <div className="text-gray-500 text-xs">DISCIPLINA</div>
                            <div>{activity.discipline}</div>
                          </div>

                          <div>
                            <div className="text-gray-500 text-xs">HORAS</div>
                            <div className="font-bold text-[#083c4c]">{activity.hours} hrs</div>
                          </div>
                        </div>

                        {activity.note && (
                          <div className="mt-4 pt-3 border-t border-gray-100">
                            <div className="text-gray-500 text-xs">NOTA</div>
                            <div>{activity.note}</div>
                          </div>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
