import React, { useState } from 'react';
import FormularioHoras from './components/FormularioHoras';
import LoginPage from './components/LoginPage';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

interface AuthenticatedUser {
  id: number;
  name: string;
}

function App() {
  const [authenticatedUser, setAuthenticatedUser] = useState<AuthenticatedUser | null>(null);

  const handleLoginSuccess = (userData: { employee_id: number; employee_name: string }) => {
    setAuthenticatedUser({ id: userData.employee_id, name: userData.employee_name });
  };

  const handleLogout = () => {
    setAuthenticatedUser(null);
  };

  const handleSuccess = () => {
    console.log("Operaci贸n exitosa, actualizando resumen...");
    // Esta funci贸n se mantiene por si es necesaria para otros componentes.
  };

  return (
    <div className="h-screen bg-gradient-to-br from-gray-50 to-blue-50 flex items-center justify-center">
      <div className="flex-grow flex flex-col p-4 sm:p-6 md:p-8">
        <ToastContainer />
        {authenticatedUser ? (
          <FormularioHoras 
            onSuccess={handleSuccess} 
            employeeId={authenticatedUser.id} 
            employeeName={authenticatedUser.name}
            onLogout={handleLogout}
          />
        ) : (
          <LoginPage onLoginSuccess={handleLoginSuccess} />
        )}
      </div>
    </div>
  );
}

export default App;
