import React, { useState, useEffect, useCallback } from 'react';
import InputField from './InputField';
import SelectField from './SelectField';
import TextareaField from './TextareaField';
import DatePicker from './DatePicker';
import { 
  submitHours, 
  getProjects, 
  getEmployees, 
  getProjectStages, 
  getDisciplinesByStage, 
  getActivitiesByDiscipline,
  getDailyActivities
} from '../api/horasApi';

interface FormularioHorasProps {
    onSuccess: () => void;
}

interface Project {
    code: string;
    name: string;
}

interface Employee {
    id: number;
    name: string;
}

interface Activity {
    id: number;
    date: string;
    employee_id: number;
    project_code: string;
    project_name: string;
    phase: string;
    discipline: string;
    activity: string;
    hours: number;
    note: string;
}

const FormularioHoras: React.FC<FormularioHorasProps> = ({ onSuccess }) => {
    // Estado inicial vacío para todos los campos
    const [formData, setFormData] = useState({
        date: '',
        employee_id: '',
        project_code: '',
        phase: '',
        discipline: '',
        activity: '',
        hours: '',
        note: ''
    });

    const [projects, setProjects] = useState<Project[]>([]);
    const [projectsMap, setProjectsMap] = useState<Record<string, string>>({});
    const [employees, setEmployees] = useState<Employee[]>([]);
    const [stages, setStages] = useState<string[]>([]);
    const [disciplines, setDisciplines] = useState<string[]>([]);
    const [activities, setActivities] = useState<string[]>([]);
    const [selectedProjectName, setSelectedProjectName] = useState('');
    const [selectedEmployeeName, setSelectedEmployeeName] = useState('');

    const [loadingProjects, setLoadingProjects] = useState(false);
    const [loadingEmployees, setLoadingEmployees] = useState(false);
    const [loadingStages, setLoadingStages] = useState(false);
    const [loadingDisciplines, setLoadingDisciplines] = useState(false);
    const [loadingActivities, setLoadingActivities] = useState(false);
    const [submitError, setSubmitError] = useState<string | null>(null);
    
    // Nuevo estado para actividades registradas
    const [dailyActivities, setDailyActivities] = useState<Activity[]>([]);
    const [loadingDailyActivities, setLoadingDailyActivities] = useState(false);

    useEffect(() => {
        const fetchInitialData = async () => {
            setLoadingProjects(true);
            try {
                const projectsData = await getProjects();
                const projectsList = projectsData.map((p: any) => ({ code: p.code, name: p.name }));
                setProjects(projectsList);
                
                const map: Record<string, string> = {};
                projectsList.forEach((project: Project) => {
                    map[project.code] = project.name;
                });
                setProjectsMap(map);
            } catch (error) {
                console.error("Fallo al obtener proyectos:", error);
            } finally {
                setLoadingProjects(false);
            }

            setLoadingEmployees(true);
            try {
                const employeesData = await getEmployees();
                setEmployees(employeesData.map((e: any) => ({ id: e.id, name: e.name })));
            } catch (error) {
                console.error("Fallo al obtener empleados:", error);
            } finally {
                setLoadingEmployees(false);
            }
        };

        fetchInitialData();
    }, []);

    // Función para cargar actividades diarias
    const fetchDailyActivities = useCallback(async () => {
        if (formData.date && formData.employee_id) {
            setLoadingDailyActivities(true);
            try {
                const activitiesData = await getDailyActivities(
                    formData.date, 
                    parseInt(formData.employee_id)
                );
                setDailyActivities(activitiesData);
            } catch (error) {
                console.error("Error al obtener actividades diarias:", error);
            } finally {
                setLoadingDailyActivities(false);
            }
        } else {
            setDailyActivities([]);
        }
    }, [formData.date, formData.employee_id]);

    // Cargar actividades cuando cambia la fecha o empleado
    useEffect(() => {
        fetchDailyActivities();
    }, [formData.date, formData.employee_id, fetchDailyActivities]);

    // Actualizar nombre de empleado seleccionado
    useEffect(() => {
        if (formData.employee_id) {
            const employee = employees.find(emp => emp.id.toString() === formData.employee_id);
            setSelectedEmployeeName(employee?.name || '');
        } else {
            setSelectedEmployeeName('');
        }
    }, [formData.employee_id, employees]);

    useEffect(() => {
        if (formData.project_code) {
            setSelectedProjectName(projectsMap[formData.project_code] || '');
        } else {
            setSelectedProjectName('');
        }

        if (formData.project_code) {
            setLoadingStages(true);
            setFormData(prev => ({ ...prev, phase: '', discipline: '', activity: '' }));
            setStages([]);
            setDisciplines([]);
            setActivities([]);
            const fetchStages = async () => {
                try {
                    const stagesData = await getProjectStages(formData.project_code);
                    setStages(stagesData);
                } catch (error) {
                    console.error(`Fallo al obtener etapas para ${formData.project_code}:`, error);
                } finally {
                    setLoadingStages(false);
                }
            };
            fetchStages();
        } else {
            setStages([]);
            setDisciplines([]);
            setActivities([]);
            setFormData(prev => ({ ...prev, phase: '', discipline: '', activity: '' }));
        }
    }, [formData.project_code, projectsMap]);

    useEffect(() => {
        if (formData.project_code && formData.phase) {
            setLoadingDisciplines(true);
            setFormData(prev => ({ ...prev, discipline: '', activity: '' }));
            setDisciplines([]);
            setActivities([]);
            const fetchDisciplines = async () => {
                try {
                    const disciplinesData = await getDisciplinesByStage(formData.project_code, formData.phase);
                    setDisciplines(disciplinesData);
                } catch (error) {
                    console.error(`Fallo al obtener disciplinas para el proyecto ${formData.project_code}, etapa ${formData.phase}:`, error);
                } finally {
                    setLoadingDisciplines(false);
                }
            };
            fetchDisciplines();
        } else {
            setDisciplines([]);
            setActivities([]);
            setFormData(prev => ({ ...prev, discipline: '', activity: '' }));
        }
    }, [formData.project_code, formData.phase]);

    useEffect(() => {
        if (formData.project_code && formData.phase && formData.discipline) {
            setLoadingActivities(true);
            setFormData(prev => ({ ...prev, activity: '' }));
            setActivities([]);
            const fetchActivities = async () => {
                try {
                    const activitiesData = await getActivitiesByDiscipline(formData.project_code, formData.phase, formData.discipline);
                    setActivities(activitiesData);
                } catch (error) {
                    console.error(`Fallo al obtener actividades para el proyecto ${formData.project_code}, etapa ${formData.phase}, disciplina ${formData.discipline}:`, error);
                } finally {
                    setLoadingActivities(false);
                }
            };
            fetchActivities();
        } else {
            setActivities([]);
            setFormData(prev => ({ ...prev, activity: '' }));
        }
    }, [formData.project_code, formData.phase, formData.discipline]);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
        setSubmitError(null);
    };

    const handleDateChange = (date: Date | null) => {
        // Convertir a string ISO (YYYY-MM-DD) para el estado
        const dateString = date ? date.toISOString().split('T')[0] : '';
        setFormData(prev => ({ ...prev, date: dateString }));
        setSubmitError(null);
    };

    const handleProjectCodeChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
        const { value } = e.target;
        setFormData(prev => ({ 
            ...prev, 
            project_code: value,
            phase: '',
            discipline: '',
            activity: ''
        }));
        setSubmitError(null);
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setSubmitError(null);

        // Validación básica
        const requiredFields = ['date', 'employee_id', 'project_code', 'phase', 'discipline', 'activity', 'hours'];
        for (const field of requiredFields) {
            if (!formData[field as keyof typeof formData]) {
                setSubmitError(`Por favor, rellena todos los campos obligatorios. Falta: ${field}`);
                return;
            }
        }

        // Obtener nombre del empleado
        const employee = employees.find(emp => emp.id.toString() === formData.employee_id);
        const employeeName = employee ? employee.name : '';

        const payload = {
            ...formData,
            employee: employeeName,
            employee_id: parseInt(formData.employee_id),
            hours: parseFloat(formData.hours)
        };

        try {
            await submitHours(payload);
            alert('Horas registradas exitosamente');
            // Resetear formulario a estado vacío
            setFormData(prev => ({
                ...prev,
                phase: '',
                discipline: '',
                activity: '',
                hours: '',
                note: ''
            }));
            
            // Recargar actividades después de registrar
            fetchDailyActivities();
            
            onSuccess();
        } catch (error: any) {
            console.error('Error al enviar el formulario:', error);
            const errorMessage = error.response?.data?.detail || "Error al registrar horas.";
            setSubmitError(errorMessage);
        }
    };

    // Convertir el string de fecha a objeto Date para el DatePicker
    const selectedDate = formData.date ? new Date(formData.date) : null;
    
    // Formatear fecha para mostrar
    const formattedDate = selectedDate ? 
        selectedDate.toLocaleDateString('es-ES', { 
            weekday: 'long', 
            day: 'numeric', 
            month: 'long', 
            year: 'numeric' 
        }) : 'Seleccione una fecha';

    return (

        <div className="max-w-4xl mx-auto p-6">
            <div className="text-center mb-8">
                <h1 className="text-5xl font-bold text-[#083c4c]">Ingenio BIM</h1>
                <p className="text-[#083c4c] font-medium">CONSTRUCCIÓN INTELIGENTE</p>
            </div>

            <div className="bg-white rounded-xl shadow-lg overflow-hidden border border-[#083c4c]">
                <div className="bg-[#083c4c] text-white p-4">
                    <h2 className="text-xl font-bold text-center">REGISTRO DE HORAS</h2>
                    <p className="text-center text-[#FBE205] text-sm">Complete el formulario para registrar sus horas de trabajo</p>
                </div>

                <div className="p-6">
                    {submitError && (
                        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-6" role="alert">
                            {submitError}
                        </div>
                    )}

                    {/* Sección de Fecha y Empleado */}
                    <div className="mb-6">
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">FECHA</label>
                                <DatePicker
                                    label="" // Corregido: label vacío para cumplir con la interfaz
                                    selectedDate={selectedDate}
                                    onChange={handleDateChange}
                                />
                            </div>
                            
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">COLABORADOR</label>
                                <SelectField
                                    label="" // Corregido: label vacío
                                    name="employee_id"
                                    value={formData.employee_id}
                                    onChange={handleChange}
                                    options={employees.map(emp => ({ value: emp.id.toString(), label: emp.name }))}
                                    disabled={loadingEmployees}
                                    isLoading={loadingEmployees}
                                />
                            </div>
                        </div>

                        <div className="bg-[#083c4c] rounded-lg p-4 border border-gray-200 text-white">
                            <div className="flex items-center mb-2">
                                <div className="text-[#FBE205] font-medium">COLABORADOR •</div>
                                <div className="ml-2 font-bold">{selectedEmployeeName || 'Seleccione un colaborador'}</div>
                            </div>
                            <div className="text-white font-bold">{formattedDate}</div>
                        </div>
                    </div>

                    {/* Sección de Proyecto */}
                    <div className="mb-6">
                        <div className="bg-[#083c4c] rounded-lg p-4 border border-gray-200 mb-4 text-white">
                            <div className="flex items-center">
                                <div className="text-[#FBE205] font-medium">PROYECTO •</div>
                                <div className="ml-2 font-bold">{selectedProjectName || 'Seleccione un proyecto'}</div>
                            </div>
                        </div>

                        <div className="grid grid-cols-1 gap-6">
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">CÓDIGO</label>
                                <SelectField
                                    label="" // Corregido: label vacío
                                    name="project_code"
                                    value={formData.project_code}
                                    onChange={handleProjectCodeChange}
                                    options={projects.map(proj => ({ value: proj.code, label: proj.code }))}
                                    disabled={loadingProjects}
                                    isLoading={loadingProjects}
                                />
                            </div>
                        </div>
                    </div>

                    {/* Sección de Etapa, Disciplina y Actividad */}
                    <div className="mb-6">
                        <div className="grid grid-cols-1 gap-6">
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">ETAPA</label>
                                <SelectField
                                    label="" // Corregido: label vacío
                                    name="phase"
                                    value={formData.phase}
                                    onChange={handleChange}
                                    options={stages.map(stage => ({ value: stage, label: stage }))}
                                    disabled={!formData.project_code || loadingStages}
                                    isLoading={loadingStages}
                                />
                            </div>
                            
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">DISCIPLINA</label>
                                <SelectField
                                    label="" // Corregido: label vacío
                                    name="discipline"
                                    value={formData.discipline}
                                    onChange={handleChange}
                                    options={disciplines.map(disc => ({ value: disc, label: disc }))}
                                    disabled={!formData.phase || loadingDisciplines}
                                    isLoading={loadingDisciplines}
                                />
                            </div>
                            
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">ACTIVIDAD</label>
                                <SelectField
                                    label="" // Corregido: label vacío
                                    name="activity"
                                    value={formData.activity}
                                    onChange={handleChange}
                                    options={activities.map(act => ({ value: act, label: act }))}
                                    disabled={!formData.discipline || loadingActivities}
                                    isLoading={loadingActivities}
                                />
                            </div>
                        </div>
                    </div>

                    {/* Sección de Horas y Nota */}
                    <div className="mb-8">
                        <div className="grid grid-cols-1 gap-6">
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">HORAS</label>
                                <InputField
                                    label="" // Corregido: label vacío
                                    name="hours"
                                    type="number"
                                    value={formData.hours}
                                    onChange={handleChange}
                                    min="0.5"
                                    step="0.5"
                                />
                            </div>
                            
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">NOTA (OPCIONAL)</label>
                                <TextareaField
                                    label="" // Corregido: label vacío
                                    name="note"
                                    value={formData.note}
                                    onChange={handleChange}
                                    rows={3}
                                />
                            </div>
                        </div>
                    </div>

                    {/* Botones */}
                    <div className="flex justify-between">
                        <button
                            type="button"
                            className="bg-gray-500 hover:bg-gray-600 text-white font-bold py-2 px-6 rounded-lg transition duration-200"
                        >
                            CANCELAR
                        </button>
                        <button
                            type="submit"
                            className="bg-[#FBE205] hover:bg-[#e0c900] text-[#083c4c] font-bold py-2 px-6 rounded-lg transition duration-200"
                            onClick={handleSubmit}
                        >
                            REGISTRAR
                        </button>
                    </div>
                </div>
            </div>

            {/* Listado de Actividades Registradas */}
            <div className="mt-10">
                <h3 className="text-xl font-bold mb-4 text-[#083c4c] border-b pb-2">LISTADO DE ACTIVIDADES</h3>
                
                {loadingDailyActivities ? (
                    <div className="text-center py-8">
                        <div className="animate-spin rounded-full h-10 w-10 border-b-2 border-[#083c4c] mx-auto"></div>
                        <p className="mt-3 text-gray-600">Cargando actividades...</p>
                    </div>
                ) : dailyActivities.length === 0 ? (
                    <div className="text-center py-8 bg-gray-50 rounded-lg">
                        <div className="text-gray-400 mb-3">
                            <svg xmlns="http://www.w3.org/2000/svg" className="h-16 w-16 mx-auto" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-3 7h3m-3 4h3m-6-4h.01M9 16h.01" />
                            </svg>
                        </div>
                        <p className="text-gray-500">No hay actividades registradas para este día</p>
                    </div>
                ) : (
                    <div className="space-y-4">
                        {dailyActivities.map((activity, index) => (
                            <div key={index} className="bg-white border rounded-lg p-5 shadow-sm border-[#083c4c]">
                                <div className="flex justify-between items-start mb-3">
                                    <h4 className="font-bold text-lg text-[#083c4c]">{activity.activity}</h4>
                                    <span className="text-sm text-gray-500">{activity.date}</span>
                                </div>
                                
                                <div className="text-sm text-gray-700">
                                    <p className="font-semibold mb-1">Proyecto {activity.project_name}</p>
                                    
                                    <div className="grid grid-cols-3 gap-4 mt-3">
                                        <div>
                                            <div className="text-gray-500 text-xs">ETAPA</div>
                                            <div>{activity.phase}</div>
                                        </div>
                                        
                                        <div>
                                            <div className="text-gray-500 text-xs">DISCIPLINA</div>
                                            <div>{activity.discipline}</div>
                                        </div>
                                        
                                        <div>
                                            <div className="text-gray-500 text-xs">HORAS</div>
                                            <div className="font-bold text-[#083c4c]">{activity.hours} hrs</div>
                                        </div>
                                    </div>
                                    
                                    {activity.note && (
                                        <div className="mt-4 pt-3 border-t border-gray-100">
                                            <div className="text-gray-500 text-xs">NOTA</div>
                                            <div>{activity.note}</div>
                                        </div>
                                    )}
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default FormularioHoras;
