    <div className="text-center py-10 px-4 bg-muted/20 rounded-lg border-2 border-dashed">
        <Clock className="h-12 w-12 mx-auto text-muted-foreground mb-3" />
        <p className="font-medium text-muted-foreground">No hay actividades registradas para hoy</p>
        <p className="text-sm text-muted-foreground mt-1">Utilice el formulario para agregar su primera actividad.</p>
    </div>
);

const TotalHoursProgress: React.FC<TotalHoursProgressProps> = ({ totalHours, selectedDate }) => {
    const dayOfWeek = getDay(selectedDate); // Domingo = 0, Lunes = 1, ..., SÃ¡bado = 6
    const maxHours = (dayOfWeek === 5) ? 8 : 9; // Viernes es 8, el resto 9

    const progress = totalHours > 0 ? Math.min((totalHours / maxHours) * 100, 100) : 0;
    const remainingHours = maxHours - totalHours;

    return (
        <div className="bg-primary/5 p-4 rounded-lg">
            <div className="flex items-center justify-between mb-1">
                <span className="text-sm font-medium">Horas trabajadas hoy:</span>
                <span className="text-sm font-semibold">{totalHours.toFixed(1)} / {maxHours.toFixed(1)} horas</span>
            </div>
            <Progress value={progress} className="h-2" />
            <p className="text-xs text-muted-foreground mt-2">
                {totalHours < maxHours
                    ? `Faltan ${remainingHours.toFixed(1)} horas para completar la jornada.`
                    : 'Â¡Jornada completa! Buen trabajo.'}
            </p>
        </div>
    );
};

