import React, { useState } from 'react';
import FormularioHoras from './components/FormularioHoras';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function App() {
  // Este estado ahora solo sirve para el ResumenHoras, podría eliminarse si no se necesita.
  const [lastUpdate, setLastUpdate] = useState(Date.now());

  const handleSuccess = () => {
    console.log("Operación exitosa, actualizando resumen...");
    setLastUpdate(Date.now());
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-blue-50 py-8 px-4">

        {/* Pasa la prop onSuccess requerida al componente FormularioHoras */}
        <FormularioHoras onSuccess={handleSuccess} />

    </div>
  );
}

export default App;
