import React, { useState } from 'react';
import FormularioHoras from './components/FormularioHoras';
import LoginPage from './components/LoginPage';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function App() {
  const [authenticatedEmployeeId, setAuthenticatedEmployeeId] = useState<number | null>(null);

  const handleLoginSuccess = (employeeId: number) => {
    setAuthenticatedEmployeeId(employeeId);
  };

  const handleLogout = () => {
    setAuthenticatedEmployeeId(null);
  };

  const handleSuccess = () => {
    console.log("Operación exitosa, actualizando resumen...");
    // Esta función se mantiene por si es necesaria para otros componentes.
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-blue-50 py-8 px-4">
      <ToastContainer />
      {authenticatedEmployeeId ? (
        <>
          <div style={{ display: 'flex', justifyContent: 'flex-end', marginBottom: '1rem' }}>
            <button 
              onClick={handleLogout} 
              className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
            >
              Cerrar Sesión
            </button>
          </div>
          <FormularioHoras 
            onSuccess={handleSuccess} 
            employeeId={authenticatedEmployeeId} 
          />
        </>
      ) : (
        <LoginPage onLoginSuccess={handleLoginSuccess} />
      )}
    </div>
  );
}

export default App;
