// Asegurar que TypeScript reconozca los módulos de imágenes
declare module '*.png' {
  const value: string;
  export default value;
}

declare module '*.jpg' {
  const value: string;
  export default value;
}

declare module '*.jpeg' {
  const value: string;
  export default value;
}

declare module '*.gif' {
  const value: string;
  export default value;
}

declare module '*.svg' {
  import * as React from 'react';
  export const ReactComponent: React.FunctionComponent<React.SVGProps<SVGSVGElement>>;
  const src: string;
  export default src;
}

// Asegurar que TypeScript reconozca los módulos CSS
// (ya está en css.d.ts, pero lo incluimos aquí para completitud)

declare module '*.module.css' {
  const classes: { [key: string]: string };
  export default classes;
}

declare module '*.module.scss' {
  const classes: { [key: string]: string };
  export default classes;
}

declare module '*.module.sass' {
  const classes: { [key: string]: string };
  export default classes;
}

// Asegurar que los alias de ruta funcionen correctamente
declare module '@/components/*';
declare module '@/lib/*';
declare module '@/styles/*';
declare module '@/utils/*';
